INSERT INTO `news` (`id`, `title`, `image`, `thumb`, `sample`, `content`, `keyword`, `order`, `datepost`, `published`, `crawl`, `parentId`, `featured`, `subId`, `hits`, `lang`, `seo_title`, `seo_description`, `seo_keyword`) VALUES
(49, 'Chuyện ''cởi trói'' cho giáo viên của Nhà giáo nhân dân', '/news/images/9/d/5/9d5f4b233af4361dd57f598ac18837a5.jpg', '', 'Từ kinh nghiệm đi dạy, Nhà giáo nhân dân Lưu Xuân Giới (Trưởng phòng GD-ĐT huyện Đông Triều, tỉnh Quảng Ninh) cho rằng, những người cơ hội, thương mại hóa giáo dục hay không chịu rèn luyện, đổi mới bản thâm thì sớm muộn sẽ nhận ra mình tụt hậu, phải tự đào thải.', '<p style="margin-top: 6px !important; margin-right: 0px; margin-bottom: 6px !important; margin-left: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; font-family: ''Times New Roman''; font-size: 16px; color: rgb(0, 0, 0); line-height: 26.6666660308838px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; orphans: auto; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(250, 250, 250);" class="content_font_class"><strong style="margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; font-family: ''Times New Roman'' !important; font-size: 12pt !important; color: rgb(0, 0, 0) !important; line-height: 20pt !important;" class="content_font_class">Từ kinh nghiệm đi dạy,&nbsp;<strong style="margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; font-family: ''Times New Roman'' !important; font-size: 12pt !important; color: rgb(0, 0, 0) !important; line-height: 20pt !important;" class="content_font_class">Nh&agrave; gi&aacute;o nh&acirc;n d&acirc;n Lưu Xu&acirc;n Giới (<strong style="margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; font-family: ''Times New Roman'' !important; font-size: 12pt !important; color: rgb(0, 0, 0) !important; line-height: 20pt !important;" class="content_font_class">Trưởng ph&ograve;ng GD-ĐT huyện Đ&ocirc;ng Triều, tỉnh Quảng Ninh</strong>)&nbsp;</strong><span style="margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; font-family: ''Times New Roman'', Times, serif; font-size: 12pt !important; color: rgb(0, 0, 0); line-height: 20pt !important;" class="content_font_class">cho rằng, những người cơ hội, thương mại h&oacute;a gi&aacute;o dục hay kh&ocirc;ng chịu r&egrave;n luyện, đổi mới bản th&acirc;m th&igrave; sớm muộn sẽ nhận ra m&igrave;nh tụt hậu, phải tự đ&agrave;o thải.</span></strong></p>\r\n<p style="margin-top: 6px !important; margin-right: 0px; margin-bottom: 6px !important; margin-left: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; font-family: ''Times New Roman''; font-size: 16px; color: rgb(0, 0, 0); line-height: 26.6666660308838px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; orphans: auto; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(250, 250, 250);" class="content_font_class">Trong danh s&aacute;ch 39 Nh&agrave; gi&aacute;o nh&acirc;n d&acirc;n được vinh danh trong dịp 20/11 năm nay, Trưởng ph&ograve;ng GD-ĐT huyện Đ&ocirc;ng Triều (Quảng Ninh) l&agrave; một trong hai người kh&ocirc;ng c&ocirc;ng t&aacute;c tại c&aacute;c trường ĐH.</p>\r\n<p style="margin-top: 6px !important; margin-right: 0px; margin-bottom: 6px !important; margin-left: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; font-family: ''Times New Roman''; font-size: 16px; color: rgb(0, 0, 0); line-height: 26.6666660308838px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; orphans: auto; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(250, 250, 250);" class="content_font_class"><strong style="margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; font-family: ''Times New Roman'' !important; font-size: 12pt !important; color: rgb(0, 0, 0) !important; line-height: 20pt !important;" class="content_font_class">Tuổi 19, ra đi kh&ocirc;ng vụ lợi</strong></p>\r\n<p style="margin-top: 6px !important; margin-right: 0px; margin-bottom: 6px !important; margin-left: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; font-family: ''Times New Roman''; font-size: 16px; color: rgb(0, 0, 0); line-height: 26.6666660308838px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; orphans: auto; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(250, 250, 250);" class="content_font_class">Năm 1974, 19 tuổi tốt nghiệp hệ trung cấp sư phạm của địa phương, thầy gi&aacute;o tiểu học Lưu Xu&acirc;n Giới kh&ocirc;ng ngần ngại xung phong l&ecirc;n x&atilde; v&ugrave;ng cao Minh Cầm thuộc huyện miền n&uacute;i Ba Chẽ (tỉnh Quảng Ninh).</p>\r\n<table width="400" cellspacing="0" cellpadding="0" align="center" style="margin-top: 6px !important; margin-bottom: 6px !important; color: rgb(0, 0, 0); font-family: ''Times New Roman''; font-size: 16px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 26.6666660308838px; orphans: auto; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(250, 250, 250);">\r\n    <tbody>\r\n        <tr>\r\n            <td style="font-family: ''Times New Roman'' !important; font-size: 12pt !important; color: rgb(0, 0, 0) !important; line-height: 20pt !important;" class="content_font_class">\r\n            <table style="font-family: ''Times New Roman'' !important; font-size: 12pt !important; color: rgb(0, 0, 0) !important; line-height: 20pt !important; margin: auto;" class="content_font_class">\r\n                <tbody>\r\n                    <tr>\r\n                        <td style="font-family: Tahoma, Geneva, sans-serif;"><img style="margin: 0px; padding: 0px; border: 0px none; outline: 0px; vertical-align: baseline; font-family: Tahoma, Geneva, sans-serif; font-size: 11px; color: rgb(81, 81, 81); height: auto;" alt="đổi mới, gi&aacute;o vi&ecirc;n, cơ hội, Nh&agrave; gi&aacute;o nh&acirc;n d&acirc;n" title="đổi mới, gi&aacute;o vi&ecirc;n, cơ hội, Nh&agrave; gi&aacute;o nh&acirc;n d&acirc;n" src="http://farm10.gox.vn/tinmoi/store/images/thumb/18112014/142/1952958/chuyen_coi_troi_cho_giao_vien_cua_nha_giao_nhan_dan_0.jpg" /></td>\r\n                    </tr>\r\n                </tbody>\r\n            </table>\r\n            </td>\r\n        </tr>\r\n        <tr style="text-align: center; font-style: italic;">\r\n            <td style="font-family: ''Times New Roman'' !important; font-size: 12pt !important; color: rgb(0, 0, 0) !important; line-height: 20pt !important;" class="content_font_class">Trưởng ph&ograve;ng Lưu Xu&acirc;n Giới tr&ecirc;n bục nhận danh hiệu Nh&agrave; gi&aacute;o nh&acirc;n d&acirc;n năm 2014. (Ảnh: Văn Chung)</td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n<p style="margin-top: 6px !important; margin-right: 0px; margin-bottom: 6px !important; margin-left: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; font-family: ''Times New Roman''; font-size: 16px; color: rgb(0, 0, 0); line-height: 26.6666660308838px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; orphans: auto; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(250, 250, 250);" class="content_font_class">&ldquo;Khi đ&oacute; m&igrave;nh biết gi&aacute;o dục địa phương đang rất thiếu gi&aacute;o vi&ecirc;n v&ugrave;ng cao. Chỉ suy nghĩ b&agrave; con nơi đ&oacute; kh&aacute;t chữ, cần kiến thức văn h&oacute;a n&ecirc;n xung phong về th&ocirc;i&rdquo; &ndash; thầy Giới chia sẻ. Từ trung t&acirc;m huyện Ba Chẽ, thầy gi&aacute;o trẻ mất 2 ng&agrave;y băng đồi n&uacute;i đến với học tr&ograve;.</p>\r\n<p style="margin-top: 6px !important; margin-right: 0px; margin-bottom: 6px !important; margin-left: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; font-family: ''Times New Roman''; font-size: 16px; color: rgb(0, 0, 0); line-height: 26.6666660308838px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; orphans: auto; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(250, 250, 250);" class="content_font_class">Bản nơi thầy Giới về c&ocirc;ng t&aacute;c khi ấy trắng điểm trường. Học sinh muốn học tiểu học phải đi bộ gần 6km đường n&uacute;i, qua v&agrave;i quả đồi mới đến điểm trường ch&iacute;nh.</p>\r\n<p style="margin-top: 6px !important; margin-right: 0px; margin-bottom: 6px !important; margin-left: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; font-family: ''Times New Roman''; font-size: 16px; color: rgb(0, 0, 0); line-height: 26.6666660308838px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; orphans: auto; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(250, 250, 250);" class="content_font_class">Vừa đặt ba l&ocirc;, t&uacute;i s&aacute;ch đến cơ sở mới, thầy Giới bắt tay v&agrave;o việc mở điểm trường mới.</p>\r\n<p style="margin-top: 6px !important; margin-right: 0px; margin-bottom: 6px !important; margin-left: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; font-family: ''Times New Roman''; font-size: 16px; color: rgb(0, 0, 0); line-height: 26.6666660308838px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; orphans: auto; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(250, 250, 250);" class="content_font_class">Vận động trẻ em đồng b&agrave;o d&acirc;n tộc T&agrave;y đến trường với thầy Giới kh&oacute; m&agrave; dễ. Chỉ đến nh&agrave; n&oacute;i con cần học c&aacute;i chữ th&igrave; kh&oacute; nhưng đến trường được vui chơi ai cũng h&agrave;o hứng.</p>\r\n<p style="margin-top: 6px !important; margin-right: 0px; margin-bottom: 6px !important; margin-left: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; font-family: ''Times New Roman''; font-size: 16px; color: rgb(0, 0, 0); line-height: 26.6666660308838px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; orphans: auto; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(250, 250, 250);" class="content_font_class">Trong từng tiết học đến những ng&agrave;y cuối tuần hay dịp lễ, Tết,&hellip;thầy Giới lu&ocirc;n kết hợp c&aacute;c tr&ograve; chơi, sinh hoạt cộng đồng với việc dạy kiến thức cho tr&ograve;.</p>\r\n<p style="margin-top: 6px !important; margin-right: 0px; margin-bottom: 6px !important; margin-left: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; font-family: ''Times New Roman''; font-size: 16px; color: rgb(0, 0, 0); line-height: 26.6666660308838px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; orphans: auto; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(250, 250, 250);" class="content_font_class">Thầy tổ chức c&aacute;c buổi thi h&aacute;t, thi bơi,&hellip;hay sinh hoạt chung cho học sinh, thậm ch&iacute; tổ chức thi c&aacute;c tr&ograve; chơi giữa c&aacute;c bản.</p>\r\n<p style="margin-top: 6px !important; margin-right: 0px; margin-bottom: 6px !important; margin-left: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; font-family: ''Times New Roman''; font-size: 16px; color: rgb(0, 0, 0); line-height: 26.6666660308838px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; orphans: auto; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(250, 250, 250);" class="content_font_class">Học sinh, nh&acirc;n d&acirc;n ai cũng hồ hởi. Học tr&ograve; từ 6 tuổi đến 30,40 tuổi cũng từ c&aacute;c bản cũng đến xin học ở điểm trường của thầy.</p>\r\n<p style="margin-top: 6px !important; margin-right: 0px; margin-bottom: 6px !important; margin-left: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; font-family: ''Times New Roman''; font-size: 16px; color: rgb(0, 0, 0); line-height: 26.6666660308838px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; orphans: auto; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(250, 250, 250);" class="content_font_class">Ngay năm đầu ti&ecirc;n điểm trường tiểu học của x&atilde; Minh Cầm nơi thầy Giới dạy đ&atilde; c&oacute; gần 100 học sinh tr&ecirc;n 6 lớp. Một con số đủ để lập một điểm trường ch&iacute;nh ở nhiều huyện miền n&uacute;i kh&aacute;c.</p>\r\n<p style="margin-top: 6px !important; margin-right: 0px; margin-bottom: 6px !important; margin-left: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; font-family: ''Times New Roman''; font-size: 16px; color: rgb(0, 0, 0); line-height: 26.6666660308838px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; orphans: auto; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(250, 250, 250);" class="content_font_class">C&oacute; học tr&ograve;, thầy lại nghĩ ra đề t&agrave;i dạy tiếng Việt cho người T&agrave;y. Bởi &ldquo;c&oacute; hiểu tiếng Việt th&igrave; tr&ograve; mới tiếp thu được kiến thức văn h&oacute;a mới&rdquo;. Dạy tiếng bắt đầu phải từ dạy từ. Thầy vận dụng từ những m&ocirc; h&igrave;nh hay tự vẽ h&igrave;nh ảnh minh hoạt dễ hiểu để giảng cho tr&ograve; hiểu như từ xa lạ.</p>\r\n<p style="margin-top: 6px !important; margin-right: 0px; margin-bottom: 6px !important; margin-left: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; font-family: ''Times New Roman''; font-size: 16px; color: rgb(0, 0, 0); line-height: 26.6666660308838px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; orphans: auto; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(250, 250, 250);" class="content_font_class">Học tr&ograve; đ&ocirc;ng, nhiều lứa tuổi thầy Giới lại nghĩ ra th&iacute; điểm v&agrave; th&agrave;nh c&ocirc;ng với đề t&agrave;i dạy lớp gh&eacute;p ở đồng b&agrave;o d&acirc;n tộc &iacute;t người. Đề t&agrave;i sau đ&oacute; được phổ biến ra to&agrave;n huyện.</p>\r\n<p style="margin-top: 6px !important; margin-right: 0px; margin-bottom: 6px !important; margin-left: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; font-family: ''Times New Roman''; font-size: 16px; color: rgb(0, 0, 0); line-height: 26.6666660308838px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; orphans: auto; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(250, 250, 250);" class="content_font_class"><strong style="margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; font-family: ''Times New Roman'' !important; font-size: 12pt !important; color: rgb(0, 0, 0) !important; line-height: 20pt !important;" class="content_font_class">Hạnh ph&uacute;c nhất trong đời gi&aacute;o vi&ecirc;n<br />\r\n</strong></p>\r\n<p style="margin-top: 6px !important; margin-right: 0px; margin-bottom: 6px !important; margin-left: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; font-family: ''Times New Roman''; font-size: 16px; color: rgb(0, 0, 0); line-height: 26.6666660308838px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; orphans: auto; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(250, 250, 250);" class="content_font_class">2 năm sau ng&agrave;y thầy Giới về với bản l&agrave;ng, điểm trường mới tại x&atilde; Minh Cầm đ&atilde; trở th&agrave;nh điểm s&aacute;ng gi&aacute;o dục của huyện Ba Chẽ.</p>\r\n<p style="margin-top: 6px !important; margin-right: 0px; margin-bottom: 6px !important; margin-left: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; font-family: ''Times New Roman''; font-size: 16px; color: rgb(0, 0, 0); line-height: 26.6666660308838px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; orphans: auto; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(250, 250, 250);" class="content_font_class">Lớp học của thầy đ&ocirc;ng từ s&aacute;ng đến 22h, hết dạy c&aacute;c lớp ch&iacute;nh thầy lại dạy c&aacute;c lớp bổ t&uacute;c. Đ&ecirc;m khuya, trong căn nh&agrave; tre nhỏ đơn sơ được b&agrave; con dựng l&ecirc;n, thầy lại say sưa soạn gi&aacute;o &aacute;n v&agrave; l&ecirc;n những &yacute; tưởng mới.</p>\r\n<p style="margin-top: 6px !important; margin-right: 0px; margin-bottom: 6px !important; margin-left: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; font-family: ''Times New Roman''; font-size: 16px; color: rgb(0, 0, 0); line-height: 26.6666660308838px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; orphans: auto; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(250, 250, 250);" class="content_font_class">Nhận thấy chuy&ecirc;n m&ocirc;n của thầy sẽ rất hợp khi về trường d&acirc;n tộc nội tr&uacute; của huyện, năm 1976 thầy Giới được điều động về l&agrave;m quản l&iacute; v&agrave; tiếp tục giảng dạy tại trường d&acirc;n tộc nội tr&uacute; của huyện c&aacute;ch đ&oacute; gần 70km.</p>\r\n<p style="margin-top: 6px !important; margin-right: 0px; margin-bottom: 6px !important; margin-left: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; font-family: ''Times New Roman''; font-size: 16px; color: rgb(0, 0, 0); line-height: 26.6666660308838px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; orphans: auto; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(250, 250, 250);" class="content_font_class">&ldquo;Nhớ lắm ng&agrave;y m&igrave;nh kho&aacute;c ba l&ocirc; ra đi, người d&acirc;n của cả bản l&agrave;ng cuốc bộ qua mấy quả n&uacute;i tiễn thầy. C&oacute; người c&ograve;n cho củ khoai, c&aacute;i b&aacute;nh. Kỉ niệm đẹp ấy c&oacute; lẽ suốt cuộc đời gi&aacute;o vi&ecirc;n m&igrave;nh chẳng thể n&agrave;o qu&ecirc;n&rdquo; &ndash; thầy Giới nhớ lại.</p>\r\n<p style="margin-top: 6px !important; margin-right: 0px; margin-bottom: 6px !important; margin-left: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; font-family: ''Times New Roman''; font-size: 16px; color: rgb(0, 0, 0); line-height: 26.6666660308838px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; orphans: auto; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(250, 250, 250);" class="content_font_class">Sau đ&oacute; thầy Giới được học l&ecirc;n cao đẳng, đến 1985 th&igrave; về dạy to&aacute;n THCS tại huyện Đ&ocirc;ng Triều nơi m&igrave;nh sinh ra.</p>\r\n<p style="margin-top: 6px !important; margin-right: 0px; margin-bottom: 6px !important; margin-left: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; font-family: ''Times New Roman''; font-size: 16px; color: rgb(0, 0, 0); line-height: 26.6666660308838px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; orphans: auto; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(250, 250, 250);" class="content_font_class">Năm 2003 thầy được cử l&agrave;m ph&oacute; ph&ograve;ng GD-ĐT của huyện, 2007 l&ecirc;n l&agrave;m trưởng ph&ograve;ng đến nay.</p>\r\n<p style="margin-top: 6px !important; margin-right: 0px; margin-bottom: 6px !important; margin-left: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; font-family: ''Times New Roman''; font-size: 16px; color: rgb(0, 0, 0); line-height: 26.6666660308838px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; orphans: auto; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(250, 250, 250);" class="content_font_class"><strong style="margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; font-family: ''Times New Roman'' !important; font-size: 12pt !important; color: rgb(0, 0, 0) !important; line-height: 20pt !important;" class="content_font_class">Lu&ocirc;n đổi mới m&igrave;nh v&agrave; &quot;cởi tr&oacute;i&quot; cho gi&aacute;o vi&ecirc;n</strong></p>\r\n<p style="margin-top: 6px !important; margin-right: 0px; margin-bottom: 6px !important; margin-left: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; font-family: ''Times New Roman''; font-size: 16px; color: rgb(0, 0, 0); line-height: 26.6666660308838px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; orphans: auto; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(250, 250, 250);" class="content_font_class">Thầy Giới t&acirc;m niệm: Lu&ocirc;n nghĩ phải l&agrave;m g&igrave; đ&oacute; mới th&igrave; gi&aacute;o dục mới đi l&ecirc;n, mới hiệu quả.</p>\r\n<p style="margin-top: 6px !important; margin-right: 0px; margin-bottom: 6px !important; margin-left: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; font-family: ''Times New Roman''; font-size: 16px; color: rgb(0, 0, 0); line-height: 26.6666660308838px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; orphans: auto; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(250, 250, 250);" class="content_font_class">Năm 1988-1990 cả nước đẩy mạnh tuy&ecirc;n truyền gi&aacute;o dục d&acirc;n số, thầy Nghĩ phải đưa v&agrave;o gi&aacute;o dục nội dung n&agrave;y trong gi&aacute;o dục v&agrave; th&agrave;nh c&ocirc;ng với chuy&ecirc;n đề gi&aacute;o dục d&acirc;n số trong nh&agrave; trường phổ th&ocirc;ng.</p>\r\n<table width="400" cellspacing="0" cellpadding="0" align="center" style="margin-top: 6px !important; margin-bottom: 6px !important; color: rgb(0, 0, 0); font-family: ''Times New Roman''; font-size: 16px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 26.6666660308838px; orphans: auto; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(250, 250, 250);">\r\n    <tbody>\r\n        <tr>\r\n            <td style="font-family: ''Times New Roman'' !important; font-size: 12pt !important; color: rgb(0, 0, 0) !important; line-height: 20pt !important;" class="content_font_class">\r\n            <table style="font-family: ''Times New Roman'' !important; font-size: 12pt !important; color: rgb(0, 0, 0) !important; line-height: 20pt !important; margin: auto;" class="content_font_class">\r\n                <tbody>\r\n                    <tr>\r\n                        <td style="font-family: Tahoma, Geneva, sans-serif;"><img style="margin: 0px; padding: 0px; border: 0px none; outline: 0px; vertical-align: baseline; font-family: Tahoma, Geneva, sans-serif; font-size: 11px; color: rgb(81, 81, 81); height: auto;" alt="đổi mới, gi&aacute;o vi&ecirc;n, cơ hội, Nh&agrave; gi&aacute;o nh&acirc;n d&acirc;n" title="đổi mới, gi&aacute;o vi&ecirc;n, cơ hội, Nh&agrave; gi&aacute;o nh&acirc;n d&acirc;n" src="http://farm10.gox.vn/tinmoi/store/images/thumb/18112014/142/1952958/chuyen_coi_troi_cho_giao_vien_cua_nha_giao_nhan_dan_1.jpg" /></td>\r\n                    </tr>\r\n                </tbody>\r\n            </table>\r\n            </td>\r\n        </tr>\r\n        <tr style="text-align: center; font-style: italic;">\r\n            <td style="font-family: ''Times New Roman'' !important; font-size: 12pt !important; color: rgb(0, 0, 0) !important; line-height: 20pt !important;" class="content_font_class">\r\n            <p style="margin-top: 6px !important; margin-right: 0px; margin-bottom: 6px !important; margin-left: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; font-family: ''Times New Roman'', Times, serif; font-size: 16px; color: rgb(0, 0, 0); line-height: 20pt !important;" class="content_font_class">Trưởng ph&ograve;ng Lưu Xu&acirc;n Giới giới thiệu v&agrave; hướng dẫn khai th&aacute;c Cơ sở dữ liệu lược đồ SGK điện tử hồi năm 2013. (Ảnh: truelife.vn)</p>\r\n            </td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n<p style="margin-top: 6px !important; margin-right: 0px; margin-bottom: 6px !important; margin-left: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; font-family: ''Times New Roman''; font-size: 16px; color: rgb(0, 0, 0); line-height: 26.6666660308838px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; orphans: auto; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(250, 250, 250);" class="content_font_class">Năm 2008 thầy khởi động đề t&agrave;i N&acirc;ng chất lượng giảng dạy ứng dụng c&ocirc;ng nghệ th&ocirc;ng tin trong dạy v&agrave; học. Năm 2011 đề t&agrave;i được ph&aacute;t triển l&ecirc;n cấp tỉnh. Đến 2013 Đề &aacute;n ph&ograve;ng GD-ĐT điện tử của thầy Giới được &aacute;p dụng tr&ecirc;n to&agrave;n tỉnh.</p>\r\n<p style="margin-top: 6px !important; margin-right: 0px; margin-bottom: 6px !important; margin-left: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; font-family: ''Times New Roman''; font-size: 16px; color: rgb(0, 0, 0); line-height: 26.6666660308838px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; orphans: auto; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(250, 250, 250);" class="content_font_class">Trước đ&oacute;, khi c&ograve;n giảng dạy to&aacute;n THCS, thầy c&oacute; đề t&agrave;i n&acirc;ng chất lượng học sinh giỏi to&aacute;n của học sinh địa phương được tỉnh đ&aacute;nh gi&aacute; cao.</p>\r\n<p style="margin-top: 6px !important; margin-right: 0px; margin-bottom: 6px !important; margin-left: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; font-family: ''Times New Roman''; font-size: 16px; color: rgb(0, 0, 0); line-height: 26.6666660308838px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; orphans: auto; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(250, 250, 250);" class="content_font_class">3 năm trở lại đ&acirc;y, tr&ecirc;n cương vị trưởng ph&ograve;ng thầy Giới chỉ đạo tất cả c&aacute;c trường phải giao quyền tự chủ lớn hơn, t&ocirc;n trọng cho gi&aacute;o vi&ecirc;n đứng lớp.</p>\r\n<p style="margin-top: 6px !important; margin-right: 0px; margin-bottom: 6px !important; margin-left: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; font-family: ''Times New Roman''; font-size: 16px; color: rgb(0, 0, 0); line-height: 26.6666660308838px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; orphans: auto; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(250, 250, 250);" class="content_font_class">Quan điểm gi&aacute;o dục phải mở, gi&aacute;o vi&ecirc;n phải tự chủ, vị trưởng ph&ograve;ng cho ph&eacute;p gi&aacute;o vi&ecirc;n mềm h&oacute;a nội dung từng b&agrave;i giảng, tiết dạy để lồng gh&eacute;p hoặc bổ trợ kiến thức, kỹ năng kh&aacute;c cần thiết cho học sinh.</p>\r\n<p style="margin-top: 6px !important; margin-right: 0px; margin-bottom: 6px !important; margin-left: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; font-family: ''Times New Roman''; font-size: 16px; color: rgb(0, 0, 0); line-height: 26.6666660308838px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; orphans: auto; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(250, 250, 250);" class="content_font_class">&ldquo;Kh&ocirc;ng ai hiểu tr&ograve; như c&aacute;c thầy c&ocirc; đứng lớp. Miễn sao thầy c&ocirc; mang lại hiệu quả, chất lượng thực sự cho tr&ograve; l&agrave; được&rdquo; &ndash; &ocirc;ng Giới khẳng định.</p>\r\n<p style="margin-top: 6px !important; margin-right: 0px; margin-bottom: 6px !important; margin-left: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; font-family: ''Times New Roman''; font-size: 16px; color: rgb(0, 0, 0); line-height: 26.6666660308838px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; orphans: auto; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(250, 250, 250);" class="content_font_class">Nhận thấy sổ s&aacute;ch, giấy tờ cũng l&agrave;m khổ gi&aacute;o vi&ecirc;n n&ecirc;n qu&aacute; tr&igrave;nh thanh-kiểm tra &ocirc;ng Giới lu&ocirc;n nhắc nhở c&aacute;n bộ ph&ograve;ng GD-ĐT chỉ tập trung v&agrave;o những loại sổ s&aacute;ch thực sự quan trọng cho gi&aacute;o vi&ecirc;n, gi&aacute;o &aacute;n l&agrave; loại sổ được xem trọng.</p>\r\n<p style="margin-top: 6px !important; margin-right: 0px; margin-bottom: 6px !important; margin-left: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; font-family: ''Times New Roman''; font-size: 16px; color: rgb(0, 0, 0); line-height: 26.6666660308838px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; orphans: auto; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(250, 250, 250);" class="content_font_class">Nhờ những &ldquo;cởi tr&oacute;i&rdquo; cho gi&aacute;o vi&ecirc;n n&ecirc;n trưởng ph&ograve;ng Lưu Xu&acirc;n Giới cho biết: &ldquo;Những năm qua gi&aacute;o dục đ&agrave;o tạo của Đ&ocirc;ng Triều lu&ocirc;n thuộc tốp đầu của tỉnh, chỉ thua khu vực trung t&acirc;m của tỉnh&rdquo;.</p>\r\n<p style="margin-top: 6px !important; margin-right: 0px; margin-bottom: 6px !important; margin-left: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; font-family: ''Times New Roman''; font-size: 16px; color: rgb(0, 0, 0); line-height: 26.6666660308838px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; orphans: auto; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(250, 250, 250);" class="content_font_class"><strong style="margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; font-family: ''Times New Roman'' !important; font-size: 12pt !important; color: rgb(0, 0, 0) !important; line-height: 20pt !important;" class="content_font_class">Người cơ hội, kh&ocirc;ng tự r&egrave;n luyện sẽ tự bị đ&agrave;o thải</strong></p>\r\n<p style="margin-top: 6px !important; margin-right: 0px; margin-bottom: 6px !important; margin-left: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; font-family: ''Times New Roman''; font-size: 16px; color: rgb(0, 0, 0); line-height: 26.6666660308838px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; orphans: auto; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(250, 250, 250);" class="content_font_class">Theo thầy Giới: &ldquo;Một người thầy nếu kh&ocirc;ng thường xuy&ecirc;n trăn trở với từng b&agrave;i giảng, tiết dạy, l&ecirc;n lớp chỉ để &ldquo;chấm c&ocirc;ng&rdquo; th&igrave; sớm muộn sẽ kh&ocirc;ng tồn tại được. Nghề kh&aacute;c c&oacute; thể l&agrave;m theo quy tr&igrave;nh, sản phẩm quen tay rồi sẽ nhanh. Nhưng nghề dạy sản phẩm l&agrave; tri thức, dạy trẻ tư duy l&uacute;c n&agrave;o cũng phải suy nghĩ. Từ sản phẩm nhỏ nhất l&agrave; b&agrave;i giảng cũng phải được chăm ch&uacute;t, ch&uacute; &yacute;, r&egrave;n rũa&rdquo;.</p>\r\n<p style="margin-top: 6px !important; margin-right: 0px; margin-bottom: 6px !important; margin-left: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; font-family: ''Times New Roman''; font-size: 16px; color: rgb(0, 0, 0); line-height: 26.6666660308838px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; orphans: auto; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(250, 250, 250);" class="content_font_class">Ng&agrave;y nay kh&ocirc;ng &iacute;t người thầy đ&atilde; l&agrave;m mất đi c&aacute;i đẹp, c&aacute;i cao qu&yacute; của nghề gi&aacute;o. Thầy Giới cho rằng những người cơ hội, thương mại h&oacute;a gi&aacute;o dục hay kh&ocirc;ng chịu r&egrave;n luyện, đổi mới m&igrave;nh th&igrave; sớm muộn sẽ nhận ra m&igrave;nh tụt hậu, phải tự đ&agrave;o thải.</p>\r\n<p style="margin-top: 6px !important; margin-right: 0px; margin-bottom: 6px !important; margin-left: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; font-family: ''Times New Roman''; font-size: 16px; color: rgb(0, 0, 0); line-height: 26.6666660308838px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; orphans: auto; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(250, 250, 250);" class="content_font_class">Đổi mới gi&aacute;o dục v&agrave; những bước đi của Bộ GD-ĐT theo thầy Giới l&agrave; cần thiết nhưng cần l&agrave;m đồng bộ với quyết t&acirc;m cao mới th&agrave;nh c&ocirc;ng. V&agrave; chắc chắn, trong qu&aacute; tr&igrave;nh ấy sự c&aacute;ch biệt giữa người l&agrave;m tốt &ndash; người chưa l&agrave;m tốt, người c&oacute; t&acirc;m huyết &ndash; người cơ hội sẽ hiện r&otilde;. Hơn bao giờ hết, đổi mới đội ngũ c&aacute;n bộ gi&aacute;o dục l&agrave; việc n&ecirc;n l&agrave;m.</p>\r\n<ul style="margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; font-family: ''Times New Roman''; font-size: 16px; color: rgb(0, 0, 0); list-style: none; line-height: 26.6666660308838px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; orphans: auto; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(250, 250, 250);" class="content_font_class">\r\n    <li style="margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; font-family: ''Times New Roman'' !important; font-size: 12pt !important; color: rgb(0, 0, 0) !important; list-style: none; line-height: 20pt !important;" class="content_font_class"><strong style="margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; font-family: ''Times New Roman'' !important; font-size: 12pt !important; color: rgb(0, 0, 0) !important; line-height: 20pt !important;" class="content_font_class">Văn Chung<span class="Apple-converted-space">&nbsp;</span></strong><em style="margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; font-family: ''Times New Roman'' !important; font-size: 12pt !important; color: rgb(0, 0, 0) !important; line-height: 20pt !important;" class="content_font_class">(ghi)</em></li>\r\n</ul>', '', 0, 1416303773, 1, 0, 0, 0, 13, 0, 1, NULL, NULL, NULL);
INSERT INTO `news` (`id`, `title`, `image`, `thumb`, `sample`, `content`, `keyword`, `order`, `datepost`, `published`, `crawl`, `parentId`, `featured`, `subId`, `hits`, `lang`, `seo_title`, `seo_description`, `seo_keyword`) VALUES
(50, 'Hạnh phúc là khi được cùng học trò ôn lại những kỉ niệm xưa', '/news/images/a/4/8/a4810b4ef2c209e1359ee6f464f760a9.jpg', '', 'Tham gia công tác giảng dạy hơn 15 năm, cô Đỗ Thị Ngọc Anh (giáo viên Trường THPT Nguyễn Trãi, Hà Nội) đã dạy nhiều thế hệ học trò và có nhiều kỉ niệm cho ngày 20/11. Mỗi lứa học trò qua đi đều để lại trong cô nhiều kỉ niệm khó quên.', '<p style="border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; margin: 0px 0px 20px; padding: 0px; box-sizing: border-box; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22.5279998779297px; orphans: auto; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background: transparent;">Tham gia c&ocirc;ng t&aacute;c giảng dạy hơn 15 năm, c&ocirc; Đỗ Thị Ngọc Anh (gi&aacute;o vi&ecirc;n Trường THPT Nguyễn Tr&atilde;i, H&agrave; Nội) đ&atilde; dạy nhiều thế hệ học tr&ograve; v&agrave; c&oacute; nhiều kỉ niệm cho ng&agrave;y 20/11. Mỗi lứa học tr&ograve; qua đi đều để lại trong c&ocirc; nhiều kỉ niệm kh&oacute; qu&ecirc;n.</p>\r\n<p style="border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; margin: 0px 0px 20px; padding: 0px; box-sizing: border-box; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22.5279998779297px; orphans: auto; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background: transparent;">Chia sẻ về kỉ niệm đ&aacute;ng nhớ nhất trong ng&agrave;y 20/11, c&ocirc; Ngọc Anh x&uacute;c động nhớ lại hơn 10 năm về trước:</p>\r\n<p style="border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; margin: 0px 0px 20px; padding: 0px; box-sizing: border-box; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22.5279998779297px; orphans: auto; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background: transparent;">&ldquo;Năm 2010, l&uacute;c đ&oacute; c&ocirc; đang tham gia c&ocirc;ng t&aacute;c giảng dạy ở Hưng Y&ecirc;n. C&ocirc; ở trong căn ph&ograve;ng nhỏ, chỉ 18m vu&ocirc;ng. H&ocirc;m 20/11, học sinh trong lớp, 30 em rủ nhau đến nh&agrave; chơi. C&ocirc; l&agrave;m 2 con g&agrave; v&agrave; miến cho c&aacute;c con ăn. Tuy nhi&ecirc;n, do ph&ograve;ng nhỏ, kh&ocirc;ng đủ chỗ ngồi, b&aacute;t đũa cũng kh&ocirc;ng đủ phải ngồi hết l&ecirc;n giường. Học sinh lần lượt thay phi&ecirc;n nhau ăn rồi rửa b&aacute;t. Vui lắm. Kỷ niệm ng&agrave;y 20/11 đ&oacute; c&ocirc; vẫn c&ograve;n nhớ m&atilde;i&rdquo;.</p>\r\n<table style="border: 1px solid rgb(238, 238, 238); outline: 0px; font-size: 14px; vertical-align: baseline; margin-top: 0px; margin-right: auto; margin-bottom: 10px !important; margin-left: auto; padding: 1px; box-sizing: border-box; border-collapse: collapse; border-spacing: 0px; width: 1px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22.5279998779297px; orphans: auto; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background: rgb(233, 239, 243);" class="picture">\r\n    <tbody style="border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; margin: 0px; padding: 0px; box-sizing: border-box; background: transparent;">\r\n        <tr style="border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; margin: 0px; padding: 0px; box-sizing: border-box; background: transparent;">\r\n            <td style="border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; margin: 0px; padding: 0px; box-sizing: border-box; background: transparent;" class="pic"><a style="border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; margin: 0px; padding: 0px; box-sizing: border-box; text-decoration: none; color: rgb(19, 103, 145); background: transparent;" href="http://img.giaoduc.net.vn/w500/Uploaded/nguyennhung/2014_11_12/994048_542606935833874_1120603262_n.jpg" class="fancybox" title="Bức ảnh kỉ niệm của c&ocirc; Ngọc Anh b&ecirc;n c&aacute;c học tr&ograve; trong ng&agrave;y 20/11" rel="gallery"><img style="border: 0px; outline: 0px; font-size: 14px; vertical-align: middle; margin: 0px; padding: 0px; box-sizing: border-box; max-width: 500px; background: transparent;" src="http://img.giaoduc.net.vn/w500/Uploaded/nguyennhung/2014_11_12/994048_542606935833874_1120603262_n.jpg" alt="" /></a></td>\r\n        </tr>\r\n        <tr style="border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; margin: 0px; padding: 0px; box-sizing: border-box; background: transparent;">\r\n            <td style="border: 0px; outline: 0px; font-size: 0.9em; vertical-align: baseline; margin: 0px; padding: 5px; box-sizing: border-box; font-style: italic; text-align: center !important; background: transparent;" class="caption">Bức ảnh kỉ niệm của c&ocirc; Ngọc Anh b&ecirc;n c&aacute;c học tr&ograve; trong ng&agrave;y 20/11</td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n<p style="border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; margin: 0px 0px 20px; padding: 0px; box-sizing: border-box; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22.5279998779297px; orphans: auto; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background: transparent;">Ng&agrave;y Nh&agrave; gi&aacute;o Việt Nam sắp tới, c&ocirc; Ngọc Anh c&oacute; mong ước: &ldquo;20/11 n&agrave;y c&ocirc; chỉ mong được ngồi b&ecirc;n những học tr&ograve; cũ để t&aacute;n gẫu, c&ugrave;ng c&aacute;c em tr&ograve; chuyện, &ocirc;n lại kỉ niệm xưa&rdquo;</p>\r\n<p style="border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; margin: 0px 0px 20px; padding: 0px; box-sizing: border-box; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22.5279998779297px; orphans: auto; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background: transparent;">Trong khi đ&oacute;, c&ocirc; Hải Thu (giảng vi&ecirc;n Đại học Ngoại ngữ - ĐH Quốc gia H&agrave; Nội) vui vẻ cho biết mong ước ng&agrave;y 20/11: &ldquo;C&ocirc; chỉ c&oacute; mong ước l&agrave; c&aacute;c em học sinh của m&igrave;nh học giỏi v&agrave; lu&ocirc;n nhớ tới m&igrave;nh&rdquo;.</p>\r\n<p style="border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; margin: 0px 0px 20px; padding: 0px; box-sizing: border-box; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22.5279998779297px; orphans: auto; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background: transparent;">Kỷ niệm 20/11 nhớ nhất của c&ocirc; Hải Thu l&agrave; v&agrave;o 20/11 năm ngo&aacute;i. Ng&agrave;y h&ocirc;m đ&oacute;, học sinh trong lớp c&ocirc; chủ nhiệm đ&atilde; l&agrave;m một clip ngắn tặng c&ocirc; (Clip l&agrave; những bức ảnh chụp c&ocirc; v&agrave; tr&ograve; trong giờ tr&ecirc;n lớp, c&aacute;c hoạt động ngoại kh&oacute;a, đi du lịch&hellip;), rồi c&aacute;c em nặn h&igrave;nh đất s&eacute;t c&ocirc; gi&aacute;o tặng c&ocirc; v&agrave; cả lớp c&ugrave;ng h&aacute;t My love. C&ocirc; đ&atilde; rất x&uacute;c động. Đến b&acirc;y giờ, những kỉ vật v&agrave; kỉ niệm ng&agrave;y h&ocirc;m đ&oacute; c&ocirc; vẫn kh&ocirc;ng thể qu&ecirc;n.</p>\r\n<div style="border-width: 1px 1px 1px 2px; border-style: solid; border-color: rgb(238, 238, 238) rgb(238, 238, 238) rgb(238, 238, 238) rgb(28, 122, 169); outline: 0px; font-size: 14px; vertical-align: baseline; margin-top: 0px; margin-right: 0px; margin-bottom: 10px !important; margin-left: 10px; padding: 10px; box-sizing: border-box; min-height: 80px; width: 200px; float: right; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22.5279998779297px; orphans: auto; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background: rgb(238, 242, 244);" class="notebox nright">\r\n<p style="border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; margin: 0px 0px 20px; padding: 0px; box-sizing: border-box; background: transparent;"><a style="border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; margin: 0px; padding: 0px; box-sizing: border-box; text-decoration: none; color: rgb(19, 103, 145); background: transparent;" href="http://giaoduc.net.vn/Giao-duc-24h/Giac-mo-len-buc-giang-chua-bao-gio-thanh-hien-thuc-post152112.gd"><img style="border: 0px; outline: 0px; font-size: 14px; vertical-align: middle; margin: 0px; padding: 0px; box-sizing: border-box; max-width: 500px; width: 175.21875px; height: auto !important; background: transparent;" src="http://img.giaoduc.net.vn/w210/Uploaded/nguyennhung/2014_11_10/image002.jpg" alt="" /></a><a style="border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; margin: 0px; padding: 0px; box-sizing: border-box; text-decoration: none; color: rgb(19, 103, 145); background: transparent;" href="http://giaoduc.net.vn/Giao-duc-24h/Giac-mo-len-buc-giang-chua-bao-gio-thanh-hien-thuc-post152112.gd">Giấc mơ l&ecirc;n bục giảng chưa bao giờ th&agrave;nh hiện thực</a></p>\r\n<p style="border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; margin: 0px 0px 20px; padding: 0px; box-sizing: border-box; background: transparent;">Ch&agrave;o mừng Ng&agrave;y Nh&agrave; gi&aacute;o Việt Nam, cảm gi&aacute;c h&aacute;o hức trong t&ocirc;i kh&ocirc;ng c&ograve;n, chỉ c&ograve;n lại cảm gi&aacute;c nuối tiếc cho giấc mơ chưa th&agrave;nh hiện thực.</p>\r\n</div>\r\n<p style="border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; margin: 0px 0px 20px; padding: 0px; box-sizing: border-box; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22.5279998779297px; orphans: auto; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background: transparent;">C&ograve;n với thầy C&ugrave; Hải Ninh (gi&aacute;o vi&ecirc;n Trường THCS N&ocirc;ng Trang, Ph&uacute; Thọ) lại chia sẻ c&acirc;u chuyện của m&igrave;nh: L&agrave; một gi&aacute;o vi&ecirc;n trẻ, mới nhận c&ocirc;ng t&aacute;c chưa l&acirc;u, v&agrave;o ng&agrave;y 20/11, thầy nhớ c&oacute; nhiều học sinh cũ giờ kh&ocirc;ng dạy nữa đến chơi. Khi đ&oacute;, thầy v&agrave; tr&ograve; c&ugrave;ng &ocirc;n lại những kỷ niệm xưa.</p>\r\n<p style="border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; margin: 0px 0px 20px; padding: 0px; box-sizing: border-box; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22.5279998779297px; orphans: auto; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background: transparent;">Tuy nhi&ecirc;n, c&oacute; một điều thầy Ninh vẫn lu&ocirc;n trăn trở: &ldquo;Học sinh b&acirc;y giờ kh&aacute;c qu&aacute;, mới học cấp 2 th&ocirc;i m&agrave; 20/11, một số em đ&atilde; biết đi phong b&igrave; thầy. Như vậy l&agrave;m mất đi vẻ truyền thống của ng&agrave;y n&agrave;y&quot;. Theo thầy Ninh, kh&ocirc;ng n&ecirc;n để c&aacute;c em học sinh c&oacute; tư tưởng về phong b&igrave; qu&aacute; sớm, như vậy ảnh hưởng đến suy nghĩ, quan điểm của c&aacute;c em về thầy c&ocirc; gi&aacute;o.</p>\r\n<p style="border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; margin: 0px 0px 20px; padding: 0px; box-sizing: border-box; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22.5279998779297px; orphans: auto; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background: transparent;">Trong khi đ&oacute;, nhớ về 20/11, c&ocirc; gi&aacute;o Trần Thu Thủy (gi&aacute;o vi&ecirc;n Trường THPT Lương Văn Can) lại bồi hồi cảm x&uacute;c khi nhớ về cậu học tr&ograve; Nguyễn Đức Giang:</p>\r\n<p style="border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; margin: 0px 0px 20px; padding: 0px; box-sizing: border-box; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22.5279998779297px; orphans: auto; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background: transparent;">&ldquo;Lớp c&ocirc; chủ nhiệm c&oacute; một học sinh bị trầm cảm. Ng&agrave;y 20/11 năm ngo&aacute;i cũng l&agrave; ng&agrave;y 20/11 cuối c&ugrave;ng ở trường cấp 3. Sau lễ m&iacute;t tinh ở trường, tất cả học sinh đ&atilde; về hết, c&aacute;c thầy c&ocirc; ở lại họp để li&ecirc;n hoan ch&uacute;c mừng. Cu cậu cứ đứng ở hanh lang khu văn ph&ograve;ng cho c&ocirc; m&atilde;i.</p>\r\n<p style="border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; margin: 0px 0px 20px; padding: 0px; box-sizing: border-box; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22.5279998779297px; orphans: auto; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background: transparent;">Đến l&uacute;c thầy gi&aacute;m thị nh&igrave;n thấy đứng ở đấy l&acirc;u qu&aacute; mới gọi gi&aacute;o vi&ecirc;n chủ nhiệm ra. C&ocirc; ra hỏi th&igrave; ấp a ấp &uacute;ng đưa cho c&ocirc; một bức tranh ch&acirc;n dung do em tự vẽ. Cậu bảo cảm ơn c&ocirc; v&igrave; đ&atilde; lu&ocirc;n động vi&ecirc;n em học vẽ v&agrave; theo đuổi đam m&ecirc; n&agrave;y. B&acirc;y giờ, Giang đ&atilde; l&agrave; sinh vi&ecirc;n đại học kiến tr&uacute;c rồi&rdquo;.</p>\r\n<table style="border: 1px solid rgb(238, 238, 238); outline: 0px; font-size: 14px; vertical-align: baseline; margin-top: 0px; margin-right: auto; margin-bottom: 10px !important; margin-left: auto; padding: 1px; box-sizing: border-box; border-collapse: collapse; border-spacing: 0px; width: 1px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22.5279998779297px; orphans: auto; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background: rgb(233, 239, 243);" class="picture">\r\n    <tbody style="border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; margin: 0px; padding: 0px; box-sizing: border-box; background: transparent;">\r\n        <tr style="border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; margin: 0px; padding: 0px; box-sizing: border-box; background: transparent;">\r\n            <td style="border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; margin: 0px; padding: 0px; box-sizing: border-box; background: transparent;" class="pic"><a style="border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; margin: 0px; padding: 0px; box-sizing: border-box; text-decoration: none; color: rgb(19, 103, 145); background: transparent;" href="http://img.giaoduc.net.vn/w500/Uploaded/nguyennhung/2014_11_12/1465182_604534056272734_562445938_n.jpg" class="fancybox" title="Bức ch&acirc;n dung Giang vẽ tặng c&ocirc; Thủy - một m&oacute;n qu&agrave; cảm ơn c&ocirc; trong ng&agrave;y 20/11 cuối c&ugrave;ng của năm học lớp 12" rel="gallery"><img style="border: 0px; outline: 0px; font-size: 14px; vertical-align: middle; margin: 0px; padding: 0px; box-sizing: border-box; max-width: 500px; background: transparent;" src="http://img.giaoduc.net.vn/w500/Uploaded/nguyennhung/2014_11_12/1465182_604534056272734_562445938_n.jpg" alt="" /></a></td>\r\n        </tr>\r\n        <tr style="border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; margin: 0px; padding: 0px; box-sizing: border-box; background: transparent;">\r\n            <td style="border: 0px; outline: 0px; font-size: 0.9em; vertical-align: baseline; margin: 0px; padding: 5px; box-sizing: border-box; font-style: italic; text-align: center !important; background: transparent;" class="caption">Bức ch&acirc;n dung Giang vẽ tặng c&ocirc; Thủy - một m&oacute;n qu&agrave; cảm ơn c&ocirc; trong ng&agrave;y 20/11 cuối c&ugrave;ng của năm học lớp 12</td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n<p style="border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; margin: 0px 0px 20px; padding: 0px; box-sizing: border-box; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22.5279998779297px; orphans: auto; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background: transparent;">Với c&ocirc; Nguyễn Thị Hải Yến (gi&aacute;o vi&ecirc;n Trường tiểu học L&ecirc; Qu&yacute; Đ&ocirc;n) kh&ocirc;ng giấu được sự x&uacute;c động khi nhớ về khoảnh khắc ng&agrave;y h&ocirc;m đ&oacute;:</p>\r\n<p style="border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; margin: 0px 0px 20px; padding: 0px; box-sizing: border-box; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22.5279998779297px; orphans: auto; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background: transparent;">&ldquo;Kỷ niệm ng&agrave;y 20/11 th&igrave; nhiều lắm. Năm n&agrave;o học sinh cũ cũng về trường thăm, rất x&uacute;c động. L&uacute;c đ&oacute; c&ocirc; c&agrave;ng thấy y&ecirc;u nghề hơn. Như năm ngo&aacute;i, c&aacute;c em học sinh lớp 5 m&igrave;nh chủ nhiệm c&ograve;n tự trang tr&iacute; lớp học, nhất định kh&ocirc;ng cho c&ocirc; v&agrave;o c&ugrave;ng. Đến khi xong xu&ocirc;i, c&ocirc; bước v&agrave;o lớp th&igrave; mỗi bạn cầm một quả b&oacute;ng bay đập nổ v&agrave; h&aacute;t ch&uacute;c mừng. C&ocirc; cảm động qu&aacute; kh&oacute;c lu&ocirc;n&rdquo;.</p>\r\n<p style="border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; margin: 0px 0px 20px; padding: 0px; box-sizing: border-box; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22.5279998779297px; orphans: auto; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background: transparent;">C&ocirc; Hải Yến cũng chia sẻ th&ecirc;m, c&ograve;n nhớ gia đ&igrave;nh của một em học sinh c&ocirc; dạy c&aacute;ch đ&acirc;y ba năm, d&ugrave; em đ&atilde; l&ecirc;n lớp lớn, kh&ocirc;ng c&ograve;n dạy nữa nhưng ba năm nay, ng&agrave;y kỉ niệm, lễ tết n&agrave;o gia đ&igrave;nh em cũng nhắn tin ch&uacute;c mừng, hỏi thăm c&ocirc; gi&aacute;o. Như thế c&ocirc; đ&atilde; thấy rất vui rồi.</p>\r\n<p style="border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; margin: 0px 0px 20px; padding: 0px; box-sizing: border-box; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22.5279998779297px; orphans: auto; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background: transparent;">&ldquo;Nh&igrave;n thấy c&aacute;c em học sinh nhớ tới m&igrave;nh, d&agrave;nh t&igrave;nh cảm cho m&igrave;nh, m&igrave;nh c&agrave;ng tự nhủ phải cố gắng hơn nữa để dạy dỗ c&aacute;c em, c&agrave;ng mong sao c&aacute;c em lu&ocirc;n chăm ngoan, học tốt&rdquo;. &ndash; c&ocirc; Yến b&agrave;y tỏ. &nbsp;</p>\r\n<p style="border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; margin: 0px 0px 20px; padding: 0px; box-sizing: border-box; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22.5279998779297px; orphans: auto; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background: transparent;">C&ugrave;ng l&agrave; gi&aacute;o vi&ecirc;n tiểu học như c&ocirc; Yến, c&ocirc; Nguyễn Thu Hương (gi&aacute;o vi&ecirc;n Trường tiểu học Bạch Hạc, Ph&uacute; Thọ) lại ấn tượng nhất l&agrave; buổi học tr&ecirc;n lớp trước h&ocirc;m m&iacute;t tinh ngập tr&agrave;n tiếng h&aacute;t của c&aacute;c học tr&ograve; nhỏ. Ng&agrave;y h&ocirc;m đ&oacute;, buổi học như say sưa, cởi mở hơn. C&aacute;c em học sinh hăng say, chắm ch&uacute; nghe b&agrave;i hơn, trong c&ocirc; cứ c&oacute; một cảm gi&aacute;c rộn r&agrave;ng kh&oacute; tả.</p>\r\n<p style="border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; margin: 0px 0px 20px; padding: 0px; box-sizing: border-box; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22.5279998779297px; orphans: auto; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background: transparent;">Thật kh&oacute; để diễn tả hết cảm x&uacute;c của thầy c&ocirc; khi nhớ về học tr&ograve; cũng như kh&oacute; c&oacute; thể n&oacute;i hết c&ocirc;ng ơn dạy dỗ của c&aacute;c thầy c&ocirc;. Xin được mượn c&acirc;u h&aacute;t trong b&agrave;i Người thầy của nhạc sĩ Nguyễn Nhất Huy thay cho lời kết: &ldquo;Dẫu đếm hết l&aacute; m&ugrave;a thu rơi, nhưng ng&agrave;n năm, l&agrave;m sao em đếm hết c&ocirc;ng ơn người thầy&hellip;&rdquo;</p>', '', 0, 1416303860, 1, 0, 0, 0, 13, 2, 1, NULL, NULL, NULL),
(51, 'Ôm mộng làm giàu, đừng theo nghề giáo', '/news/images/9/d/b/2_bao tri bao duong phu tung xe oto.png', '', 'Ngày ấy, bạn bè cùng trang lứa với tôi không giấu được niềm tự hào khi lần lượt nhận được giấy báo trúng tuyển vào nhiều trường Đại học danh tiếng như Bách khoa, Kinh tế, Xây dựng, Y khoa… Riêng tôi, tôi hạnh phúc với ước mơ nhỏ bé của mình và khiêm tốn bước vào trường Sư phạm với hoài bão lớn lao.', '<p style="border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; margin: 0px 0px 20px; padding: 0px; box-sizing: border-box; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22.5279998779297px; orphans: auto; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background: transparent;"><strong style="border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; margin: 0px; padding: 0px; box-sizing: border-box; background: transparent;">Dạy chữ để dạy người&hellip;</strong></p>\r\n<p style="border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; margin: 0px 0px 20px; padding: 0px; box-sizing: border-box; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22.5279998779297px; orphans: auto; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background: transparent;">Ng&agrave;y ấy, bạn b&egrave; c&ugrave;ng trang lứa với t&ocirc;i kh&ocirc;ng giấu được niềm tự h&agrave;o khi lần lượt nhận được giấy b&aacute;o tr&uacute;ng tuyển v&agrave;o nhiều trường Đại học danh tiếng như B&aacute;ch khoa, Kinh tế, X&acirc;y dựng, Y khoa&hellip; Ri&ecirc;ng t&ocirc;i, t&ocirc;i hạnh ph&uacute;c với ước mơ nhỏ b&eacute; của m&igrave;nh v&agrave; khi&ecirc;m tốn bước v&agrave;o trường Sư phạm với ho&agrave;i b&atilde;o lớn lao.</p>\r\n<div class="notebox nright" style="border-width: 1px 1px 1px 2px; border-style: solid; border-color: rgb(238, 238, 238) rgb(238, 238, 238) rgb(238, 238, 238) rgb(28, 122, 169); outline: 0px; font-size: 14px; vertical-align: baseline; margin-top: 0px; margin-right: 0px; margin-bottom: 10px !important; margin-left: 10px; padding: 10px; box-sizing: border-box; min-height: 80px; width: 200px; float: right; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22.5279998779297px; orphans: auto; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background: rgb(238, 242, 244);">\r\n<p style="border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; margin: 0px 0px 20px; padding: 0px; box-sizing: border-box; background: transparent;"><a style="border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; margin: 0px; padding: 0px; box-sizing: border-box; text-decoration: none; color: rgb(19, 103, 145); background: transparent;" href="http://giaoduc.net.vn/Giao-duc-24h/Cha-me-cai-nhau-thay-co-lo-lang-post151986.gd"><img alt="" style="border: 0px; outline: 0px; font-size: 14px; vertical-align: middle; margin: 0px; padding: 0px; box-sizing: border-box; max-width: 500px; width: 175.21875px; height: auto !important; background: transparent;" src="http://img.giaoduc.net.vn/w210/Uploaded/nguyennhung/2014_11_06/hocsinhtoangiaoducnetvn.jpg" /></a><a style="border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; margin: 0px; padding: 0px; box-sizing: border-box; text-decoration: none; color: rgb(19, 103, 145); background: transparent;" href="http://giaoduc.net.vn/Giao-duc-24h/Cha-me-cai-nhau-thay-co-lo-lang-post151986.gd">Cha mẹ &quot;c&atilde;i nhau&quot;, thầy c&ocirc; lo lắng</a></p>\r\n<p style="border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; margin: 0px 0px 20px; padding: 0px; box-sizing: border-box; background: transparent;">(GDVN) - &ldquo;Thế l&agrave; từ tuần sau m&igrave;nh kh&ocirc;ng phải tranh thủ từng giờ từng ph&uacute;t tr&ecirc;n lớp để chấm chữa b&agrave;i về nh&agrave; cho học sinh nữa rồi. Nh&agrave;n hẳn, nhưng lại thấy lo...&quot;</p>\r\n</div>\r\n<p style="border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; margin: 0px 0px 20px; padding: 0px; box-sizing: border-box; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22.5279998779297px; orphans: auto; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background: transparent;">H&agrave;nh trang đơn sơ của t&ocirc;i l&agrave; niềm tin v&agrave; l&yacute; tưởng nghề nghiệp chưa bao giờ nguội tắt giữa những kh&oacute; khăn của cuộc sống xa nh&agrave; chốn th&agrave;nh thị. Học tập ở m&ocirc;i trường Sư phạm, ch&uacute;ng t&ocirc;i - những gi&aacute;o sinh trẻ tuổi cứ thế h&agrave;ng ng&agrave;y lớn l&ecirc;n dưới sự r&egrave;n giũa kh&ocirc;ng mệt mỏi, đầy tr&aacute;ch nhiệm của đội ngũ giảng vi&ecirc;n gi&agrave;u nhiệt huyết. Ch&uacute;ng t&ocirc;i vừa học nghề, nghĩa l&agrave; trang bị kiến thức chuy&ecirc;n m&ocirc;n, nghiệp vụ như bao ng&agrave;nh nghề kh&aacute;c trước khi bước v&agrave;o cuộc sống lao động thực thụ. Tuy nhi&ecirc;n, quan trọng hơn hết l&agrave; học l&agrave;m người, học để trở th&agrave;nh một con người ch&acirc;n ch&iacute;nh, xứng đ&aacute;ng với trọng tr&aacute;ch, thi&ecirc;n chức x&atilde; hội giao ph&oacute; trong tương lai.&nbsp;</p>\r\n<p style="border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; margin: 0px 0px 20px; padding: 0px; box-sizing: border-box; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22.5279998779297px; orphans: auto; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background: transparent;">Thế n&ecirc;n, so với nhiều trường đại học kh&aacute;c, nội quy của sinh vi&ecirc;n Sư phạm cũng c&oacute; những chuẩn mực ri&ecirc;ng. Từ trang phục đến giảng đường, c&aacute;ch đi đứng, ăn n&oacute;i cho đến nếp sống, sinh hoạt&hellip; tất cả đều phải chỉn chu, m&ocirc; phạm.&nbsp;</p>\r\n<p style="border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; margin: 0px 0px 20px; padding: 0px; box-sizing: border-box; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22.5279998779297px; orphans: auto; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background: transparent;">Ch&iacute;nh v&igrave; lẽ đ&oacute;, thầy c&ocirc; gi&aacute;o trường Sư phạm cũng lu&ocirc;n tỏ ra khắt khe, nguy&ecirc;n tắc trong qu&aacute; tr&igrave;nh dạy học, gi&aacute;o dục cũng như uốn nắn nh&acirc;n c&aacute;ch cho thế hệ kế cận. Ch&uacute;ng t&ocirc;i dần trưởng th&agrave;nh qua những th&aacute;ng năm như thế v&agrave; thầm cảm ơn những thầy c&ocirc; gi&aacute;o của m&igrave;nh đ&atilde; t&ocirc;i luyện, tiếp th&ecirc;m sức mạnh, nghị lực để ng&agrave;y mai c&oacute; thể tận t&acirc;m, tận lực cống hiến cho nghề, cho l&yacute; tưởng trồng người cao đẹp.</p>\r\n<p style="border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; margin: 0px 0px 20px; padding: 0px; box-sizing: border-box; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22.5279998779297px; orphans: auto; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background: transparent;">C&oacute; lẽ, kỉ niệm kh&oacute; qu&ecirc;n nhất của những sinh vi&ecirc;n sư phạm l&agrave; ng&agrave;y đầu đứng lớp trong đợt thực tập cuối kh&oacute;a. Với tư c&aacute;ch nh&agrave; gi&aacute;o, được đứng tr&ecirc;n bục giảng, giảng b&agrave;i cho những học tr&ograve; đầu ti&ecirc;n&hellip; t&ocirc;i tin rằng, đ&oacute; l&agrave; những cảm x&uacute;c đẹp nhất, khoảnh khắc thi&ecirc;ng li&ecirc;ng nhất của bất cứ gi&aacute;o vi&ecirc;n n&agrave;o mỗi khi hồi tưởng lại. V&agrave; đ&oacute; cũng l&agrave; ng&agrave;y m&agrave; những gi&aacute;o sinh sư phạm thực sự cảm thấy y&ecirc;u v&agrave; tr&acirc;n trọng hơn bao giờ hết nghề nghiệp của m&igrave;nh.</p>\r\n<p style="border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; margin: 0px 0px 20px; padding: 0px; box-sizing: border-box; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22.5279998779297px; orphans: auto; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background: transparent;"><strong style="border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; margin: 0px; padding: 0px; box-sizing: border-box; background: transparent;">Buồn vui nghề gi&aacute;o&hellip;</strong></p>\r\n<p style="border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; margin: 0px 0px 20px; padding: 0px; box-sizing: border-box; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22.5279998779297px; orphans: auto; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background: transparent;">Thấm thoắt, t&ocirc;i đ&atilde; c&oacute; gần 5 năm gắn b&oacute; c&ugrave;ng bảng đen, phấn trắng với c&aacute;c thế hệ học tr&ograve;. Cũng chừng ấy thời gian, t&ocirc;i đ&atilde; kinh qua v&agrave; chứng kiến những thăng trầm, buồn vui nghề gi&aacute;o.</p>\r\n<p style="border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; margin: 0px 0px 20px; padding: 0px; box-sizing: border-box; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22.5279998779297px; orphans: auto; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background: transparent;">T&ocirc;i hạnh ph&uacute;c, tự h&agrave;o với những b&agrave;i giảng hay khiến học tr&ograve; say m&ecirc;, h&agrave;o hứng, qu&ecirc;n cả giờ ra chơi. T&ocirc;i h&atilde;nh diện v&igrave; học tr&ograve; đạt điểm cao m&ocirc;n học m&igrave;nh phụ tr&aacute;ch. C&oacute; những thế hệ đ&atilde; tốt nghiệp ra trường, h&agrave;ng năm đến ng&agrave;y lễ tết vẫn gh&eacute; về thăm hay điện thoại ch&uacute;c mừng. C&oacute; nhiều em lập gia đ&igrave;nh cũng gửi thiệp mời đ&aacute;m cưới&hellip; V&agrave; tất nhi&ecirc;n, kh&ocirc;ng tr&aacute;nh khỏi những thất vọng, hụt hẫng mỗi khi học tr&ograve; ch&acirc;y lười, trốn học, bị điểm k&eacute;m, thậm ch&iacute; l&agrave; ngang t&agrave;ng, ngỗ ngược, bỏ ngo&agrave;i tai những lời khuy&ecirc;n răn.</p>\r\n<div class="notebox nright" style="border-width: 1px 1px 1px 2px; border-style: solid; border-color: rgb(238, 238, 238) rgb(238, 238, 238) rgb(238, 238, 238) rgb(28, 122, 169); outline: 0px; font-size: 14px; vertical-align: baseline; margin-top: 0px; margin-right: 0px; margin-bottom: 10px !important; margin-left: 10px; padding: 10px; box-sizing: border-box; min-height: 80px; width: 200px; float: right; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22.5279998779297px; orphans: auto; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background: rgb(238, 242, 244);"><a style="border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; margin: 0px; padding: 0px; box-sizing: border-box; text-decoration: none; color: rgb(19, 103, 145); background: transparent;" href="http://giaoduc.net.vn/Giao-duc-24h/Doi-moi-giao-duc-chuong-trinh-truoc-sach-giao-khoa-sau-post151988.gd"><img alt="" style="border: 0px; outline: 0px; font-size: 14px; vertical-align: middle; margin: 0px; padding: 0px; box-sizing: border-box; max-width: 500px; width: 175.21875px; height: auto !important; background: transparent;" src="http://img.giaoduc.net.vn/w210/Uploaded/xuantrung/2014_11_07/SGKdoimoigiaoducnetvn1jpg.jpg" /></a><a style="border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; margin: 0px; padding: 0px; box-sizing: border-box; text-decoration: none; color: rgb(19, 103, 145); background: transparent;" href="http://giaoduc.net.vn/Giao-duc-24h/Doi-moi-giao-duc-chuong-trinh-truoc-sach-giao-khoa-sau-post151988.gd">Đổi mới gi&aacute;o dục: chương tr&igrave;nh trước, s&aacute;ch gi&aacute;o khoa sau</a><br />\r\n&nbsp;<time style="border: 0px; outline: 0px; font-size: 0.8em; vertical-align: baseline; margin: 0px; padding: 0px; box-sizing: border-box; color: rgb(153, 153, 153); font-weight: bold; line-height: 1.5em; background: transparent;"></time>(GDVN) - Nhiều &yacute; kiến chuy&ecirc;n gia gi&aacute;o dục, c&aacute;c nh&agrave; gi&aacute;o l&atilde;o th&agrave;nh đều khẳng định t&iacute;nh quan trọng trong việc x&acirc;y dựng một chương tr&igrave;nh chuẩn trước khi nghĩ tới l&agrave;m SGK</div>\r\n<p style="border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; margin: 0px 0px 20px; padding: 0px; box-sizing: border-box; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22.5279998779297px; orphans: auto; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background: transparent;">Mặc d&ugrave; vậy, nghề gi&aacute;o đ&atilde; dạy cho t&ocirc;i l&ograve;ng ki&ecirc;n nhẫn, sự bao dung, độ lượng v&agrave; niềm tin v&agrave;o con người lớn hơn bất cứ nghề nghiệp n&agrave;o. Chẳng thế m&agrave; nhiều l&uacute;c học tr&ograve; hư khiến t&ocirc;i bức x&uacute;c, giận dữ, nhưng thay v&igrave; qu&aacute;t th&aacute;o, đe nẹt, tư c&aacute;ch nghề gi&aacute;o đ&atilde; gi&uacute;p t&ocirc;i biết c&aacute;ch &ocirc;n tồn khuy&ecirc;n nhủ, động vi&ecirc;n, tạo cơ hội cho c&aacute;c em khắc phục sai lầm để tiến bộ.</p>\r\n<p style="border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; margin: 0px 0px 20px; padding: 0px; box-sizing: border-box; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22.5279998779297px; orphans: auto; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background: transparent;">Thầy c&ocirc; gi&aacute;o cũng l&agrave; con người, n&ecirc;n cũng c&oacute; những cung bậc cảm x&uacute;c tr&aacute;i chiều, cũng phải sống, phải mưu sinh. V&agrave; ắt hẳn ai cũng biết, hiện tại, đời sống của một bộ phận kh&ocirc;ng nhỏ gi&aacute;o vi&ecirc;n đang ở mức trung b&igrave;nh v&agrave; thấp.&nbsp;</p>\r\n<p style="border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; margin: 0px 0px 20px; padding: 0px; box-sizing: border-box; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22.5279998779297px; orphans: auto; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background: transparent;">T&ocirc;i vừa ngưỡng mộ, lại vừa thấy x&oacute;t xa cho những đồng nghiệp của m&igrave;nh l&agrave; những gi&aacute;o vi&ecirc;n cắm bản, họ hi sinh cả tuổi trẻ, quyền lợi ri&ecirc;ng tư, kh&ocirc;ng m&agrave;ng vật chất để mang chữ đến v&ugrave;ng s&acirc;u, l&ecirc;n v&ugrave;ng cao cho trẻ em ngh&egrave;o. T&ocirc;i thương cảm đến ứa nước mắt khi chứng kiến đồng nghiệp của m&igrave;nh phải qua s&ocirc;ng dạy học bằng&hellip; t&uacute;i ni l&ocirc;ng với bao rủi ro cho t&iacute;nh mạng m&agrave; kh&ocirc;ng lời ca th&aacute;n&hellip;</p>\r\n<p style="border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; margin: 0px 0px 20px; padding: 0px; box-sizing: border-box; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22.5279998779297px; orphans: auto; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background: transparent;">Chợt nhớ tới lời của một ai đ&oacute; từng nhận định như một triết l&yacute;: &ldquo;Những người &ocirc;m mộng l&agrave;m gi&agrave;u sẽ kh&ocirc;ng theo nghề gi&aacute;o, c&ograve;n những người chọn nghề gi&aacute;o th&igrave; kh&ocirc;ng tham vọng l&agrave;m gi&agrave;u&hellip;&rdquo;.</p>\r\n<p style="border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; margin: 0px 0px 20px; padding: 0px; box-sizing: border-box; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22.5279998779297px; orphans: auto; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background: transparent;">Thật vậy, vẫn biết sẽ c&ograve;n v&ocirc; v&agrave;n kh&oacute; khăn, thiếu thốn trong cuộc sống h&agrave;ng ng&agrave;y của những con người mang sứ mệnh &ldquo;g&otilde; đầu trẻ&rdquo;, trong bối cảnh hiện tại, thậm ch&iacute; họ phải bươn chải nhiều nghề để tồn tại. Tuy nhi&ecirc;n, hơn ai hết, họ lu&ocirc;n &yacute; thức được tr&aacute;ch nhiệm, thi&ecirc;n chức thi&ecirc;ng li&ecirc;ng m&agrave; x&atilde; hội giao ph&oacute; cho m&igrave;nh.&nbsp;</p>\r\n<p style="border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; margin: 0px 0px 20px; padding: 0px; box-sizing: border-box; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22.5279998779297px; orphans: auto; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background: transparent;">Đ&oacute; ch&iacute;nh l&agrave; động lực để những &ldquo;kĩ sư t&acirc;m hồn&rdquo; h&ocirc;m nay v&agrave; ng&agrave;y mai vẫn tiếp tục miệt m&agrave;i c&ugrave;ng trang gi&aacute;o &aacute;n, lặng lẽ ch&egrave;o l&aacute;i, đưa c&aacute;c thế hệ học tr&ograve; cặp bến bờ tri thức. T&ocirc;i hiểu họ đ&atilde;, đang hạnh ph&uacute;c v&agrave; tự h&agrave;o với danh xưng m&agrave; kh&ocirc;ng phải ng&agrave;nh nghề n&agrave;o cũng được phong tặng, danh xưng &ldquo;NGHỀ CAO QU&Yacute;!&rdquo;.</p>', '', 0, 1416303993, 1, 0, 0, 0, 13, 3, 1, NULL, NULL, NULL);
INSERT INTO `news` (`id`, `title`, `image`, `thumb`, `sample`, `content`, `keyword`, `order`, `datepost`, `published`, `crawl`, `parentId`, `featured`, `subId`, `hits`, `lang`, `seo_title`, `seo_description`, `seo_keyword`) VALUES
(52, 'Hoa, quà hay phong bì cho ngày Nhà giáo Việt Nam?', '/news/images/6/b/d/2_bao tri bao duong phu tung xe oto.png', '', 'Cứ đến ngày lễ gì lớn như 14/2, 8/3, 20/10, 20/11…, đi ngoài đường đâu đâu cũng thấy hoa là hoa. Đủ các loại hoa với màu sắc sặc sỡ. Hoa được bày bán từ trước ngày lễ đó có khi đến 2,3 tuần.', '<p style="border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; margin: 0px 0px 20px; padding: 0px; box-sizing: border-box; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22.5279998779297px; orphans: auto; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background: transparent;">Cứ đến ng&agrave;y lễ g&igrave; lớn như 14/2, 8/3, 20/10, 20/11&hellip;, đi ngo&agrave;i đường đ&acirc;u đ&acirc;u cũng thấy hoa l&agrave; hoa. Đủ c&aacute;c loại hoa với m&agrave;u sắc sặc sỡ. Hoa được b&agrave;y b&aacute;n từ trước ng&agrave;y lễ đ&oacute; c&oacute; khi đến 2,3 tuần.</p>\r\n<p style="border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; margin: 0px 0px 20px; padding: 0px; box-sizing: border-box; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22.5279998779297px; orphans: auto; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background: transparent;">Chẳng thế mới c&oacute; b&agrave;i h&aacute;t &ldquo;M&ugrave;ng t&aacute;m th&aacute;ng ba/ Em ra thăm vườn/ Chọn một b&ocirc;ng hoa/ Xinh tươi tặng c&ocirc; gi&aacute;o&rdquo; (tr&iacute;ch B&ocirc;ng hoa mừng c&ocirc; &ndash; Trần Thị Duy&ecirc;n). Thế mới biết hoa c&oacute; vai tr&ograve; quan trọng thế n&agrave;o trong dịp lễ cũng như nhu cầu tặng hoa trong c&aacute;c dịp lễ lớn đến mức n&agrave;o.</p>\r\n<p style="border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; margin: 0px 0px 20px; padding: 0px; box-sizing: border-box; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22.5279998779297px; orphans: auto; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background: transparent;">Ng&agrave;y Nh&agrave; gi&aacute;o Việt Nam c&ograve;n chưa tới, vậy m&agrave; giờ đi đường đ&acirc;u đ&acirc;u cũng thấy b&agrave;y b&aacute;n hoa l&agrave; hoa, đặc biệt l&agrave; trước cổng c&aacute;c trường học. Một b&oacute; hoa t&ugrave;y v&agrave;o số lượng, loại hoa m&agrave; c&oacute; những mức gi&aacute; kh&aacute;c nhau, c&ograve;n người mua cũng t&ugrave;y v&agrave;o điều kiện, mục đ&iacute;ch của m&igrave;nh m&agrave; lựa chọn.</p>\r\n<table class="picture" style="border: 1px solid rgb(238, 238, 238); outline: 0px; font-size: 14px; vertical-align: baseline; margin-top: 0px; margin-right: auto; margin-bottom: 10px !important; margin-left: auto; padding: 1px; box-sizing: border-box; border-collapse: collapse; border-spacing: 0px; width: 1px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22.5279998779297px; orphans: auto; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background: rgb(233, 239, 243);">\r\n    <tbody style="border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; margin: 0px; padding: 0px; box-sizing: border-box; background: transparent;">\r\n        <tr style="border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; margin: 0px; padding: 0px; box-sizing: border-box; background: transparent;">\r\n            <td class="pic" style="border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; margin: 0px; padding: 0px; box-sizing: border-box; background: transparent;"><a rel="gallery" title="Đủ c&aacute;c loại hoa được b&agrave;y b&aacute;n tr&ecirc;n đường trước ng&agrave;y Nh&agrave; gi&aacute;o Việt Nam. Ảnh minh họa. Nguồn: internet" class="fancybox" style="border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; margin: 0px; padding: 0px; box-sizing: border-box; text-decoration: none; color: rgb(19, 103, 145); background: transparent;" href="http://img.giaoduc.net.vn/w500/Uploaded/nguyennhung/2014_11_17/1350712633201029.jpg"><img alt="" style="border: 0px; outline: 0px; font-size: 14px; vertical-align: middle; margin: 0px; padding: 0px; box-sizing: border-box; max-width: 500px; background: transparent;" src="http://img.giaoduc.net.vn/w500/Uploaded/nguyennhung/2014_11_17/1350712633201029.jpg" /></a></td>\r\n        </tr>\r\n        <tr style="border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; margin: 0px; padding: 0px; box-sizing: border-box; background: transparent;">\r\n            <td class="caption" style="border: 0px; outline: 0px; font-size: 0.9em; vertical-align: baseline; margin: 0px; padding: 5px; box-sizing: border-box; font-style: italic; text-align: center !important; background: transparent;">Đủ c&aacute;c loại hoa được b&agrave;y b&aacute;n tr&ecirc;n đường trước ng&agrave;y Nh&agrave; gi&aacute;o Việt Nam. Ảnh minh họa. Nguồn: internet</td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n<p style="border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; margin: 0px 0px 20px; padding: 0px; box-sizing: border-box; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22.5279998779297px; orphans: auto; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background: transparent;">Tặng hoa cho thầy c&ocirc; gi&aacute;o nh&acirc;n ng&agrave;y Nh&agrave; gi&aacute;o Việt Nam cũng l&agrave; để tỏ l&ograve;ng tri &acirc;n đến c&ocirc;ng ơn dạy dỗ của thầy c&ocirc;. Nếu chỉ tặng hoa th&ocirc;i th&igrave; kh&ocirc;ng n&oacute;i l&agrave;m g&igrave;, nhưng dường như ng&agrave;y nay, việc tặng hoa th&ocirc;i chưa đủ.</p>\r\n<p style="border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; margin: 0px 0px 20px; padding: 0px; box-sizing: border-box; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22.5279998779297px; orphans: auto; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background: transparent;">Tại nhiều nơi, cứ đến những ng&agrave;y lễ lớn thầy c&ocirc; đặc biệt l&agrave; 20/11, c&aacute;c phụ huynh lại &ldquo;đau đầu&rdquo; suy nghĩ xem n&ecirc;n mua qu&agrave; g&igrave; cho thầy c&ocirc;&hellip; Với t&acirc;m l&yacute; tặng qu&agrave; để con m&igrave;nh được quan t&acirc;m hơn, kh&ocirc;ng &iacute;t phụ huynh đ&aacute;nh đồng gi&aacute; trị m&oacute;n qu&agrave; với sự quan t&acirc;m.</p>\r\n<p style="border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; margin: 0px 0px 20px; padding: 0px; box-sizing: border-box; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22.5279998779297px; orphans: auto; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background: transparent;">Qu&agrave; to qu&agrave; nhỏ, gi&aacute; trị m&oacute;n qu&agrave; bao nhi&ecirc;u tiền&hellip; cũng lại phụ thuộc v&agrave;o mục đ&iacute;ch v&agrave; điều kiện của người tặng.</p>\r\n<div class="notebox nright" style="border-width: 1px 1px 1px 2px; border-style: solid; border-color: rgb(238, 238, 238) rgb(238, 238, 238) rgb(238, 238, 238) rgb(28, 122, 169); outline: 0px; font-size: 14px; vertical-align: baseline; margin-top: 0px; margin-right: 0px; margin-bottom: 10px !important; margin-left: 10px; padding: 10px; box-sizing: border-box; min-height: 80px; width: 200px; float: right; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22.5279998779297px; orphans: auto; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background: rgb(238, 242, 244);">\r\n<p style="border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; margin: 0px 0px 20px; padding: 0px; box-sizing: border-box; background: transparent;"><a style="border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; margin: 0px; padding: 0px; box-sizing: border-box; text-decoration: none; color: rgb(19, 103, 145); background: transparent;" href="http://giaoduc.net.vn/Xa-hoi/Di-gieo-mam-tu-te--Chon-dat-pho-hay-lang-post152310.gd"><img alt="" style="border: 0px; outline: 0px; font-size: 14px; vertical-align: middle; margin: 0px; padding: 0px; box-sizing: border-box; max-width: 500px; width: 175.21875px; height: auto !important; background: transparent;" src="http://img.giaoduc.net.vn/w210/Uploaded/minhdo/2014_11_15/chi_muc.jpg" /></a><a style="border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; margin: 0px; padding: 0px; box-sizing: border-box; text-decoration: none; color: rgb(19, 103, 145); background: transparent;" href="http://giaoduc.net.vn/Xa-hoi/Di-gieo-mam-tu-te--Chon-dat-pho-hay-lang-post152310.gd">Đi gieo mầm tử tế - Chọn đất phố hay l&agrave;ng?</a></p>\r\n<p style="border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; margin: 0px 0px 20px; padding: 0px; box-sizing: border-box; background: transparent;">Ch&ugrave;m thơ dưới đ&acirc;y l&agrave; của Tiến sỹ Dương Xu&acirc;n Th&agrave;nh, một người thầy c&oacute; 40 năm tr&ecirc;n bục giảng th&acirc;n tặng độc giả nh&acirc;n ng&agrave;y 20/11/2014.</p>\r\n</div>\r\n<p style="border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; margin: 0px 0px 20px; padding: 0px; box-sizing: border-box; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22.5279998779297px; orphans: auto; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background: transparent;">Kh&ocirc;ng chỉ mua qu&agrave;, giờ đ&acirc;y, người ta c&ograve;n c&oacute; phong tr&agrave;o d&ugrave;ng &ldquo;phong b&igrave;&rdquo; thay cho lời tri &acirc;n trong ng&agrave;y 20/11. Nhưng trong x&atilde; hội n&agrave;y, cứ nhắc đến hai chữ &ldquo;phong b&igrave;&rdquo;, người ta lại nghĩ ngay l&agrave; h&agrave;nh vi &ldquo;hối lộ, mua chuộc&rdquo;.</p>\r\n<p style="border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; margin: 0px 0px 20px; padding: 0px; box-sizing: border-box; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22.5279998779297px; orphans: auto; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background: transparent;">Vậy hoa, qu&agrave; hay phong b&igrave; cho ng&agrave;y Nh&agrave; gi&aacute;o Việt Nam?</p>\r\n<p style="border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; margin: 0px 0px 20px; padding: 0px; box-sizing: border-box; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22.5279998779297px; orphans: auto; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background: transparent;">Lại nhớ đến việc một trường mầm non ở TP HCM mới đ&acirc;y c&oacute; ra th&ocirc;ng b&aacute;o kh&ocirc;ng nhận tất cả c&aacute;c loại qu&agrave; dưới bất k&igrave; h&igrave;nh thức n&agrave;o nh&acirc;n dịp lễ 20/11.</p>\r\n<p style="border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; margin: 0px 0px 20px; padding: 0px; box-sizing: border-box; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22.5279998779297px; orphans: auto; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background: transparent;">Hay như Bộ Gi&aacute;o dục v&agrave; Đ&agrave;o tạo c&oacute; ra c&ocirc;ng văn ng&agrave;y 14/11 gửi c&aacute;c bộ, ng&agrave;nh; c&aacute;c đo&agrave;n thể Trung ương; UBND c&aacute;c tỉnh, th&agrave;nh phố trực thuộc Trung ương; c&aacute;c trường đại học, học viện, viện; c&aacute;c trường đại học, cao đẳng, trung cấp chuy&ecirc;n nghiệp; c&aacute;c sở gi&aacute;o dục v&agrave; đ&agrave;o tạo; c&aacute;c cơ quan th&ocirc;ng tấn b&aacute;o ch&iacute; nhấn mạnh &ldquo;vui mừng được nhận &ldquo;<em style="border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; margin: 0px; padding: 0px; box-sizing: border-box; background: transparent;">Thiếp ch&uacute;c mừng điện tử tại địa chỉ hộp thư</em><span class="Apple-converted-space">&nbsp;</span><em style="border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; margin: 0px; padding: 0px; box-sizing: border-box; background: transparent;"><span style="border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; margin: 0px; padding: 0px; box-sizing: border-box; text-decoration: underline; background: transparent;"><a style="border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; margin: 0px; padding: 0px; box-sizing: border-box; text-decoration: none; color: rgb(19, 103, 145); background: transparent;" href="mailto:bogddt@moet.edu.vn">bogddt@moet.edu.vn</a></span></em>&rdquo; v&agrave; &ldquo;chủ trương kh&ocirc;ng tổ chức tiếp kh&aacute;ch, kh&ocirc;ng nhận hoa ch&uacute;c mừng tại trụ sở cơ quan Bộ v&agrave; Cơ quan Đại diện của Bộ tại th&agrave;nh phố Hồ Ch&iacute; Minh&rdquo;.</p>\r\n<table class="picture" style="border: 1px solid rgb(238, 238, 238); outline: 0px; font-size: 14px; vertical-align: baseline; margin-top: 0px; margin-right: auto; margin-bottom: 10px !important; margin-left: auto; padding: 1px; box-sizing: border-box; border-collapse: collapse; border-spacing: 0px; width: 1px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22.5279998779297px; orphans: auto; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background: rgb(233, 239, 243);">\r\n    <tbody style="border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; margin: 0px; padding: 0px; box-sizing: border-box; background: transparent;">\r\n        <tr style="border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; margin: 0px; padding: 0px; box-sizing: border-box; background: transparent;">\r\n            <td class="pic" style="border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; margin: 0px; padding: 0px; box-sizing: border-box; background: transparent;"><a rel="gallery" title=".Hoa, qu&agrave; hay phong b&igrave; tặng thầy c&ocirc; chỉ l&agrave; h&igrave;nh thức kh&aacute;c nhau, quan trọng l&agrave; xuất ph&aacute;t từ tấm l&ograve;ng. (Ảnh minh họa). Nguồn: ĐTNCSHCM NT" class="fancybox" style="border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; margin: 0px; padding: 0px; box-sizing: border-box; text-decoration: none; color: rgb(19, 103, 145); background: transparent;" href="http://img.giaoduc.net.vn/w500/Uploaded/nguyennhung/2014_11_17/dantoc201309051802.jpg"><img alt="" style="border: 0px; outline: 0px; font-size: 14px; vertical-align: middle; margin: 0px; padding: 0px; box-sizing: border-box; max-width: 500px; background: transparent;" src="http://img.giaoduc.net.vn/w500/Uploaded/nguyennhung/2014_11_17/dantoc201309051802.jpg" /></a></td>\r\n        </tr>\r\n        <tr style="border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; margin: 0px; padding: 0px; box-sizing: border-box; background: transparent;">\r\n            <td class="caption" style="border: 0px; outline: 0px; font-size: 0.9em; vertical-align: baseline; margin: 0px; padding: 5px; box-sizing: border-box; font-style: italic; text-align: center !important; background: transparent;">.Hoa, qu&agrave; hay phong b&igrave; tặng thầy c&ocirc; chỉ l&agrave; h&igrave;nh thức kh&aacute;c nhau, quan trọng l&agrave; xuất ph&aacute;t từ tấm l&ograve;ng. (Ảnh minh họa). Nguồn: ĐTNCSHCM NT</td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n<p style="border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; margin: 0px 0px 20px; padding: 0px; box-sizing: border-box; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22.5279998779297px; orphans: auto; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background: transparent;">Trước đ&oacute;, Sở Gi&aacute;o dục v&agrave; Đ&agrave;o tạo TP Hồ Ch&iacute; Minh cũng c&oacute; văn bản đề nghị được nhận thiệp điện tử ch&uacute;c mừng ng&agrave;y Nh&agrave; gi&aacute;o Việt Nam 20/11, kh&ocirc;ng nhận hoa, thiệp giấy, hạn chế c&aacute;c đơn vị đến ch&uacute;c mừng. L&yacute; do được đưa ra để tr&aacute;nh việc l&atilde;ng ph&iacute;.</p>\r\n<p style="border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; margin: 0px 0px 20px; padding: 0px; box-sizing: border-box; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22.5279998779297px; orphans: auto; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background: transparent;">Phải chăng người ta lo sợ những biến tướng trong ng&agrave;y Nh&agrave; gi&aacute;o Việt Nam &ndash; ng&agrave;y lễ tri &acirc;n c&aacute;c thầy c&ocirc;.</p>\r\n<div class="notebox nright" style="border-width: 1px 1px 1px 2px; border-style: solid; border-color: rgb(238, 238, 238) rgb(238, 238, 238) rgb(238, 238, 238) rgb(28, 122, 169); outline: 0px; font-size: 14px; vertical-align: baseline; margin-top: 0px; margin-right: 0px; margin-bottom: 10px !important; margin-left: 10px; padding: 10px; box-sizing: border-box; min-height: 80px; width: 200px; float: right; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22.5279998779297px; orphans: auto; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background: rgb(238, 242, 244);">\r\n<p style="border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; margin: 0px 0px 20px; padding: 0px; box-sizing: border-box; background: transparent;"><a style="border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; margin: 0px; padding: 0px; box-sizing: border-box; text-decoration: none; color: rgb(19, 103, 145); background: transparent;" href="http://giaoduc.net.vn/Giao-duc-24h/Hanh-phuc-la-khi-duoc-cung-hoc-tro-on-lai-nhung-ki-niem-xua-post152204.gd"><img alt="" style="border: 0px; outline: 0px; font-size: 14px; vertical-align: middle; margin: 0px; padding: 0px; box-sizing: border-box; max-width: 500px; width: 175.21875px; height: auto !important; background: transparent;" src="http://img.giaoduc.net.vn/w210/Uploaded/nguyennhung/2014_11_12/994048_542606935833874_1120603262_n.jpg" /></a><a style="border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; margin: 0px; padding: 0px; box-sizing: border-box; text-decoration: none; color: rgb(19, 103, 145); background: transparent;" href="http://giaoduc.net.vn/Giao-duc-24h/Hanh-phuc-la-khi-duoc-cung-hoc-tro-on-lai-nhung-ki-niem-xua-post152204.gd">Hạnh ph&uacute;c l&agrave; khi được c&ugrave;ng học tr&ograve; &ocirc;n lại những kỉ niệm xưa</a></p>\r\n<p style="border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; margin: 0px 0px 20px; padding: 0px; box-sizing: border-box; background: transparent;">Với c&aacute;c thầy c&ocirc; gi&aacute;o, những kỷ niệm về lũ học tr&ograve; &quot;nhất quỷ nh&igrave; ma&quot; nhưng gi&agrave;u t&igrave;nh cảm d&agrave;nh cho thầy c&ocirc; lu&ocirc;n l&agrave; kỷ niệm đẹp, kh&oacute; qu&ecirc;n.</p>\r\n</div>\r\n<p style="border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; margin: 0px 0px 20px; padding: 0px; box-sizing: border-box; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22.5279998779297px; orphans: auto; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background: transparent;">Nếu như trước đ&acirc;y, người ta chỉ tặng thầy c&ocirc; b&ocirc;ng hoa, những g&igrave; nh&agrave; nu&ocirc;i trồng được, rồi dần dần mua hoa, mua qu&agrave; để tặng. Cho đến b&acirc;y giờ c&oacute; điều kiện hơn, người ta lại suy nghĩ kh&ocirc;ng biết thầy c&ocirc; th&iacute;ch g&igrave;, cứ đi tiền cho nhanh, thầy c&ocirc; th&iacute;ch th&igrave; sẽ tự mua&hellip;</p>\r\n<p style="border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; margin: 0px 0px 20px; padding: 0px; box-sizing: border-box; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22.5279998779297px; orphans: auto; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background: transparent;">Dường như mỗi thời mỗi kh&aacute;c, người ta lại c&oacute; những h&igrave;nh thức kh&aacute;c nhau để tỏ l&ograve;ng tri &acirc;n trong ng&agrave;y lễ 20/11. Bởi hoa, qu&agrave; hay phong b&igrave; th&igrave; cũng đều l&agrave; d&ugrave;ng tiền của người tặng m&agrave; th&ocirc;i.</p>\r\n<p style="border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; margin: 0px 0px 20px; padding: 0px; box-sizing: border-box; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22.5279998779297px; orphans: auto; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background: transparent;">Thiết nghĩ việc tặng hoa, qu&agrave; hay phong b&igrave; cũng kh&ocirc;ng phải việc g&igrave; xấu, quan trọng l&agrave; tấm l&ograve;ng của người tặng ra sao, đừng để gi&aacute; trị vật chất từ những b&ocirc;ng hoa, m&oacute;n qu&agrave; hay chiếc phong b&igrave; l&agrave;m mất đi &yacute; nghĩa nh&acirc;n văn của ng&agrave;y lễ tri &acirc;n thầy c&ocirc;.</p>\r\n<p style="border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; margin: 0px 0px 20px; padding: 0px; box-sizing: border-box; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22.5279998779297px; orphans: auto; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background: transparent;">Hoa, qu&agrave; hay phong b&igrave; chỉ n&ecirc;n l&agrave; tượng trưng, c&oacute; cũng được m&agrave; kh&ocirc;ng cũng đừng n&ecirc;n suy nghĩ. Việc nhớ ơn thầy c&ocirc; cần phải xuất ph&aacute;t từ tấm l&ograve;ng, sự th&agrave;nh t&acirc;m, để người tặng cảm thấy thoải m&aacute;i, kh&ocirc;ng bị &aacute;p lực, c&ograve;n người nhận cũng kh&ocirc;ng phải trăn trở nhận hay kh&ocirc;ng nhận.</p>\r\n<p style="border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; margin: 0px 0px 20px; padding: 0px; box-sizing: border-box; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22.5279998779297px; orphans: auto; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background: transparent;">Truyền thống t&ocirc;n sư trọng đạo của người Việt Nam c&oacute; từ l&acirc;u đời, h&atilde;y cứ để học tr&ograve;, phụ huynh được b&agrave;y tỏ l&ograve;ng tri &acirc;n đối với c&aacute;c thầy c&ocirc; gi&aacute;o, nhưng đừng l&agrave;m sai lệch đi &yacute; nghĩa của ng&agrave;y lễ n&agrave;y.</p>', '', 0, 1416304192, 1, 0, 0, 0, 13, 4, 1, NULL, NULL, NULL);
INSERT INTO `news` (`id`, `title`, `image`, `thumb`, `sample`, `content`, `keyword`, `order`, `datepost`, `published`, `crawl`, `parentId`, `featured`, `subId`, `hits`, `lang`, `seo_title`, `seo_description`, `seo_keyword`) VALUES
(53, 'Thầy giáo trẻ khó lấy vợ vì nghèo', '/news/images/b/7/1/1431087303_non-bo.jpg', '', 'Sau khi đọc bài “Thư gửi chồng của nữ giáo viên nhân ngày 20/11” của cô giáo Đỗ Sông Hương, cũng là một giáo viên nên tôi thấy bài viết thật hay, thật đúng với thực trạng hiện nay. Vì thế tôi cũng mạo muội viết thêm một chút để bổ sung những điều cô chưa kể hết. Cũng là để những người không là giáo viên hiểu và thông cảm hơn cho chúng tôi.', '<p class="Normal" style="margin: 0px 0px 1em; padding: 0px; line-height: 18px; color: rgb(51, 51, 51); font-family: arial; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; orphans: auto; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255);"><span style="margin: 0px; padding: 0px;">Sau khi đọc b&agrave;i &ldquo;Thư gửi chồng của nữ gi&aacute;o vi&ecirc;n nh&acirc;n ng&agrave;y 20/11&rdquo; của c&ocirc; gi&aacute;o Đỗ S&ocirc;ng Hương, cũng l&agrave; một gi&aacute;o vi&ecirc;n n&ecirc;n t&ocirc;i thấy b&agrave;i viết thật hay, thật đ&uacute;ng với thực trạng hiện nay. V&igrave; thế t&ocirc;i cũng mạo muội viết th&ecirc;m một ch&uacute;t để bổ sung những điều c&ocirc; chưa kể hết. Cũng l&agrave; để những người kh&ocirc;ng l&agrave; gi&aacute;o vi&ecirc;n hiểu v&agrave; th&ocirc;ng cảm hơn cho ch&uacute;ng t&ocirc;i.</span></p>\r\n<p class="Normal" style="margin: 0px 0px 1em; padding: 0px; line-height: 18px; color: rgb(51, 51, 51); font-family: arial; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; orphans: auto; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255);">C&aacute;ch đ&acirc;y hơn chục năm, t&ocirc;i l&agrave; học sinh chẳng phải xuất sắc nhưng cũng gọi l&agrave; giỏi bước v&agrave;o cổng trường sư phạm với bao mơ ước ho&agrave;i b&atilde;o. Trong khi bạn nam th&igrave; thi kiến tr&uacute;c, x&acirc;y dựng, b&aacute;ch khoa, c&ocirc;ng nghiệp th&igrave; t&ocirc;i lại chọn sư phạm. Ng&agrave;y ấy trường sư phạm c&oacute; gi&aacute; lắm, điểm đầu v&agrave;o trừ mấy khoa m&ocirc;n phụ th&igrave; lu&ocirc;n thuộc tốp trường c&oacute; điểm cao.</p>\r\n<p class="Normal" style="margin: 0px 0px 1em; padding: 0px; line-height: 18px; color: rgb(51, 51, 51); font-family: arial; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; orphans: auto; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255);">T&ocirc;i chọn sư phạm ngo&agrave;i niềm y&ecirc;u th&iacute;ch th&igrave; c&ograve;n v&igrave; học sư phạm bớt đi khoản học ph&iacute;, ra trường lại được về qu&ecirc; hương c&ocirc;ng t&aacute;c, với đồng lương gi&aacute;o vi&ecirc;n như vậy ở n&ocirc;ng th&ocirc;n cũng sống kh&aacute; tốt rồi, được x&atilde; hội coi trọng v&agrave; xin việc cũng dễ v&igrave; gi&aacute;o vi&ecirc;n c&ograve;n thiếu nhiều. Thế nhưng c&aacute;i nghề t&ocirc;i v&agrave; x&atilde; hội vẫn coi l&agrave; cao qu&yacute; nhất trong những nghề cao qu&yacute; ấy đ&atilde; thay đổi nhanh ch&oacute;ng. Sự ph&aacute;t triển của c&aacute;c ng&agrave;nh kinh tế khiến cho đồng lương gi&aacute;o vi&ecirc;n trở n&ecirc;n nhỏ b&eacute; qu&aacute;.</p>\r\n<p class="Normal" style="margin: 0px 0px 1em; padding: 0px; line-height: 18px; color: rgb(51, 51, 51); font-family: arial; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; orphans: auto; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255);">Thời điểm đ&oacute; phong tr&agrave;o đi học sư phạm kh&aacute; nhiều v&igrave; thế khi t&ocirc;i ra trường cũng l&agrave; thời điểm gi&aacute;o vi&ecirc;n đang thừa rất nhiều. Với số điểm ra trường chẳng phải xuất sắc, chẳng c&oacute; v&agrave;i chục triệu v&agrave; cũng chẳng c&oacute; c&aacute;i &ldquo;&ocirc;&rdquo; n&agrave;o n&ecirc;n t&ocirc;i kh&ocirc;ng được v&agrave;o bi&ecirc;n chế. Vậy l&agrave; đ&agrave;nh nộp hồ sơ v&agrave;o một trường b&aacute;n c&ocirc;ng v&agrave; một chặng đường vi h&agrave;nh gian kh&oacute; bắt đầu để t&ocirc;i nhận thấy nghề gi&aacute;o cũng lắm gian nan hơn mọi người vẫn nghĩ.</p>\r\n<table cellspacing="0" cellpadding="3" border="0" align="center" class="tplCaption" style="margin: 0px auto 10px; padding: 0px; max-width: 100%; color: rgb(51, 51, 51); font-family: arial; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: normal; orphans: auto; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; width: 480px; background-color: rgb(255, 255, 255);">\r\n    <tbody style="margin: 0px; padding: 0px;">\r\n        <tr style="margin: 0px; padding: 0px;">\r\n            <td style="margin: 0px; padding: 0px; line-height: 0;"><img alt="thaygiao-1143-1416281387.jpg" data-natural-="" data-width="500" data-pwidth="480" style="margin: 0px; padding: 0px; border: 0px; font-size: 0px; line-height: 0; max-width: 100%; width: 480px;" src="http://m.f29.img.vnecdn.net/2014/11/18/thaygiao-1143-1416281387.jpg" /></td>\r\n        </tr>\r\n        <tr style="margin: 0px; padding: 0px;">\r\n            <td style="margin: 0px; padding: 0px; line-height: 0;">\r\n            <p class="Image" style="margin: 0px; padding: 10px; line-height: normal; font-weight: 400; font-style: normal; font-variant: normal; font-stretch: normal; font-size: 12px; font-family: arial; color: rgb(51, 51, 51); background: rgb(245, 245, 245);">Lớp học gh&eacute;p của học sinh huyện X&iacute;n Mần (H&agrave; Giang). Ảnh:<span class="Apple-converted-space">&nbsp;</span><em style="margin: 0px; padding: 0px;">Qu&yacute; Đo&agrave;n.</em></p>\r\n            </td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n<p class="Normal" style="margin: 0px 0px 1em; padding: 0px; line-height: 18px; color: rgb(51, 51, 51); font-family: arial; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; orphans: auto; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255);">Ai cũng biết &quot;nhất quỷ, nh&igrave; ma, thứ ba học tr&ograve;&quot;. Học tr&ograve; phải nghịch rồi, học tr&ograve; trường c&ocirc;ng lập nghịch một th&igrave; học tr&ograve; trường b&aacute;n c&ocirc;ng tư thục nghịch gấp 10 lần. Nghịch l&agrave; một chuyện, nhiều em lại c&ograve;n hư nữa. Những em ngoan, giỏi đ&atilde; v&agrave;o trường c&ocirc;ng lập rồi th&igrave; đương nhi&ecirc;n c&aacute;c em dốt nhất, hư nhất v&agrave;o b&aacute;n c&ocirc;ng v&agrave; d&acirc;n lập. Mặc d&ugrave; c&ograve;n nhiều em học kh&aacute; tốt v&agrave; ngoan, nhưng chiếm số &iacute;t. Ấy vậy l&agrave; đ&aacute;ng lẽ gi&aacute;o vi&ecirc;n l&ecirc;n lớp để dạy th&igrave; đằng n&agrave;y l&ecirc;n lớp ngo&agrave;i việc dạy c&ograve;n phải quản l&yacute; học sinh.</p>\r\n<p class="Normal" style="margin: 0px 0px 1em; padding: 0px; line-height: 18px; color: rgb(51, 51, 51); font-family: arial; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; orphans: auto; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255);">Ở nh&agrave; chỉ cần một đứa con hư đ&atilde; khản cố qu&aacute;t m&agrave; chẳng ăn thua huống chi ở đ&acirc;y một lớp khoảng 20 đứa con hư nhất của x&atilde; hội m&agrave; c&aacute;c phụ huynh gửi gắm nhờ thầy c&ocirc; dạy dỗ. Ng&agrave;y xưa c&ocirc; gi&aacute;o vụt cho v&agrave;i roi học sinh sợ xanh mặt, nhưng giờ vụt cho v&agrave;i roi gặp học sinh đầu gấu hoặc gặp mấy b&aacute;c phụ huynh kh&ocirc;ng hiểu chuyện l&agrave; gi&aacute;o vi&ecirc;n xanh mặt rồi.</p>\r\n<p class="Normal" style="margin: 0px 0px 1em; padding: 0px; line-height: 18px; color: rgb(51, 51, 51); font-family: arial; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; orphans: auto; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255);">Cũng dạy chương tr&igrave;nh ấy, nhưng với đối tượng học sinh như vậy đ&aacute;ng lẽ lương phải cao hơn mới phải, nhưng t&ocirc;i v&agrave; bao thầy c&ocirc; kh&aacute;c chỉ nhận được đồng lương hợp đồng b&egrave;o bọt. Kh&ocirc;ng những vậy vừa dạy vừa lo bị cho nghỉ việc l&uacute;c n&agrave;o kh&ocirc;ng hay. C&aacute;c c&ocirc; gi&aacute;o th&igrave; cũng chẳng sao bởi lương thấp th&igrave; đ&atilde; c&oacute; chồng lo rồi. Nhưng t&ocirc;i l&agrave; con trai, l&agrave; trụ cột gia đ&igrave;nh. Th&acirc;n m&igrave;nh c&ograve;n nu&ocirc;i chưa xong n&oacute;i g&igrave; nu&ocirc;i vợ nu&ocirc;i con.</p>\r\n<p class="Normal" style="margin: 0px 0px 1em; padding: 0px; line-height: 18px; color: rgb(51, 51, 51); font-family: arial; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; orphans: auto; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255);">Cũng ch&iacute;nh v&igrave; thế m&agrave; t&ocirc;i cũng như bao thầy gi&aacute;o trẻ kh&aacute;c đ&atilde; đ&agrave;nh ngậm ng&ugrave;i nh&igrave;n người y&ecirc;u l&ecirc;n xe hoa với những ch&agrave;ng trai kh&aacute;c m&agrave; chưa chắc họ đ&atilde; y&ecirc;u, nhưng họ c&oacute; nhiều tiền hơn. Kh&ocirc;ng phải họ kh&ocirc;ng y&ecirc;u m&igrave;nh nhưng cuộc sống giờ đổi kh&aacute;c. Người phụ nữ cần một người chồng c&oacute; c&ocirc;ng việc ổn định, thu nhập cao để họ kh&ocirc;ng khổ, con họ kh&ocirc;ng khổ d&ugrave; phải &ldquo;hy sinh đời mẹ&rdquo; để n&acirc;ng bước tương lai c&aacute;c con. V&igrave; thế m&agrave; c&ocirc; gi&aacute;o th&igrave; dễ lấy chồng m&agrave; thầy gi&aacute;o lại kh&oacute; lấy vợ.</p>\r\n<p class="Normal" style="margin: 0px 0px 1em; padding: 0px; line-height: 18px; color: rgb(51, 51, 51); font-family: arial; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; orphans: auto; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255);">C&oacute; nhiều bạn bảo sao kh&ocirc;ng bỏ nghề t&igrave;m c&ocirc;ng việc kh&aacute;c thu nhập cao. Cuộc sống đ&acirc;u đơn giản vậy. Cầm c&aacute;i bằng sư phạm th&igrave; đi đ&acirc;u xin việc được. Hơn nữa bố mẹ cho tiền 4 năm ăn học n&ecirc;n giờ đang đi dạy m&agrave; n&oacute;i bỏ th&igrave; bố mẹ đ&acirc;u c&oacute; cho, c&aacute;c cụ vẫn nghĩ nghề gi&aacute;o cao qu&yacute; lắm, nhưng đ&acirc;u c&oacute; biết ngo&agrave;i x&atilde; hội người ta kh&ocirc;ng trọng nữa rồi. Đến nh&agrave; bạn g&aacute;i chơi biết m&igrave;nh l&agrave; gi&aacute;o vi&ecirc;n họ qu&yacute; v&igrave; đạo đức tốt, nhưng bảo lấy con g&aacute;i họ th&igrave; họ chẳng cho v&igrave; biết m&igrave;nh ngh&egrave;o.</p>\r\n<p class="Normal" style="margin: 0px 0px 1em; padding: 0px; line-height: 18px; color: rgb(51, 51, 51); font-family: arial; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; orphans: auto; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255);">Nhưng cũng chẳng tr&aacute;ch được v&igrave; nếu m&igrave;nh c&oacute; con chắc cũng vậy th&ocirc;i. Giờ đa số những em học trung b&igrave;nh kh&aacute; mới thi sư phạm để chống trượt c&ograve;n c&aacute;c em giỏi thi ng&acirc;n h&agrave;ng, thương mại, y... Dạy ở m&ocirc;i trường m&agrave; số học sinh hư nếu đuổi học th&igrave; đuổi qu&aacute; nửa như vậy đ&acirc;u tr&aacute;nh khỏi đ&ocirc;i l&uacute;c n&oacute;ng l&ecirc;n bạt tai, hay cho em n&agrave;o đ&oacute; v&agrave;i c&aacute;i roi. V&agrave; chẳng may cho thầy c&ocirc; n&agrave;o bị quay v&agrave; đưa l&ecirc;n mạng l&agrave; sự nghiệp c&ocirc;ng danh đổ xuống s&ocirc;ng xuống biển v&agrave; nổi tiếng hơn cả C&ocirc;ng Phượng, Tuấn Anh&hellip; chỉ sau một đ&ecirc;m.</p>\r\n<p class="Normal" style="margin: 0px 0px 1em; padding: 0px; line-height: 18px; color: rgb(51, 51, 51); font-family: arial; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; orphans: auto; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255);">Mọi người v&agrave;o ph&ecirc; ph&aacute;n, chỉ tr&iacute;ch, nhưng nếu họ ở trong ho&agrave;n cảnh th&igrave; mới hiểu được. Những người chỉ ch&iacute;ch, ph&ecirc; ph&aacute;n l&agrave; những người từng học, dạy học ở những ng&ocirc;i trường học sinh ngoan. Họ đ&acirc;u c&oacute; biết v&igrave; sao thầy c&ocirc; lại bạt tai, mắng học sinh nặng lời như thế. Vẫn biết như vậy l&agrave; kh&ocirc;ng đ&uacute;ng, l&agrave; sai luật gi&aacute;o dục, nhưng khi học sinh ngồi trong lớp n&oacute;i chuyện tự do, chửi nhau, gi&aacute;o vi&ecirc;n nhắc nhở cũng chẳng coi ra g&igrave;, c&oacute; khi c&ograve;n chửi lại gi&aacute;o vi&ecirc;n. Xin hỏi c&aacute;c bạn như vậy liệu c&oacute; ức chế kh&ocirc;ng, ng&agrave;y n&agrave;o cũng trải qua như vậy th&igrave; d&ugrave; l&agrave; một người b&igrave;nh tĩnh nhất cũng c&oacute; l&uacute;c c&oacute; những h&agrave;nh động kh&ocirc;ng đ&uacute;ng chuẩn gi&aacute;o vi&ecirc;n.</p>\r\n<p class="Normal" style="margin: 0px 0px 1em; padding: 0px; line-height: 18px; color: rgb(51, 51, 51); font-family: arial; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; orphans: auto; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255);">Học sinh kh&ocirc;ng sợ điểm, kh&ocirc;ng sợ lưu ban, kh&ocirc;ng sợ đuổi học, nhẹ nh&agrave;ng cũng kh&ocirc;ng nghe th&igrave; xin hỏi phải dọa bằng c&aacute;ch n&agrave;o đ&acirc;y, những l&uacute;c như thế đ&ocirc;i khi cho v&agrave;i roi lại c&oacute; hiệu quả. Tất nhi&ecirc;n kh&ocirc;ng đến mức bạo lực như mọi người nghĩ, kh&ocirc;ng phải tra tấn như thời trung cổ m&agrave; nhiều bạn b&igrave;nh luận. Mấy năm qua c&oacute; nhiều thầy c&ocirc; bị đưa l&ecirc;n mạng v&igrave; mắng, đ&aacute;nh học sinh. Rồi họ bị kỷ luật, bị đuổi việc, ai từng trải qua đều th&ocirc;ng cảm cho c&aacute;c thầy c&ocirc;. Chỉ c&oacute; những người kh&ocirc;ng hiểu chuyện, l&uacute;c n&agrave;o cũng nghĩ gi&aacute;o vi&ecirc;n l&agrave; phải thế n&agrave;y, phải thế kia&hellip;nhưng gi&aacute;o vi&ecirc;n cũng l&agrave; con người m&agrave; đ&atilde; l&agrave; con người th&igrave; ai cũng c&oacute; hỷ, nộ, &aacute;i, ố n&ecirc;n sao tr&aacute;nh khỏi n&oacute;ng giận m&agrave; l&agrave;m những việc như vậy.</p>\r\n<p class="Normal" style="margin: 0px 0px 1em; padding: 0px; line-height: 18px; color: rgb(51, 51, 51); font-family: arial; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; orphans: auto; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255);">&Aacute;p lực l&agrave; vậy đ&oacute;. &Aacute;p lực từ cuộc sống, từ mưu sinh đ&egrave; nặng l&ecirc;n đ&ocirc;i vai gi&aacute;o vi&ecirc;n. Ai cũng nghĩ gi&aacute;o vi&ecirc;n an nh&agrave;n lắm. C&oacute; khi một tuần l&ecirc;n trường 4 buổi c&ograve;n 3 buổi ở nh&agrave;, nhưng ở nh&agrave; đ&acirc;u c&oacute; được chơi như mọi người nghĩ. N&agrave;o l&agrave; gi&aacute;o &aacute;n, chấm b&agrave;i, v&agrave;o điểm, sổ s&aacute;ch c&aacute;c loại m&agrave; trong số h&agrave;ng chục loại sổ s&aacute;ch hồ sơ th&igrave; qu&aacute; nửa chỉ để kiểm tra v&agrave; giết thời gian của gi&aacute;o vi&ecirc;n v&igrave; n&oacute; được sinh ra l&agrave; c&oacute; mục đ&iacute;ch, nhưng thực tế chẳng để l&agrave;m g&igrave; cả. Hơn nữa mỗi năm sở gi&aacute;o dục hay bộ lại cải c&aacute;ch, lại thay đổi, một v&agrave;i loại sổ s&aacute;ch lại ra đời v&agrave; thế l&agrave; gi&aacute;o vi&ecirc;n lại cắm đầu v&agrave;o l&agrave;m sổ s&aacute;ch để sở về kiểm tra.</p>\r\n<p class="Normal" style="margin: 0px 0px 1em; padding: 0px; line-height: 18px; color: rgb(51, 51, 51); font-family: arial; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; orphans: auto; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255);">Trong gi&aacute;o dục đ&atilde; kiểm tra chẳng khi n&agrave;o được khen m&agrave; chỉ c&oacute; ch&ecirc; th&ocirc;i, tốt rồi cũng phải l&ocirc;i ra một c&aacute;i g&igrave; đ&oacute; để ch&ecirc;. Chẳng may sai một c&aacute;i g&igrave; đ&oacute; l&agrave; mai được n&ecirc;u gương to&agrave;n tỉnh. Năm nay c&aacute;c em tiểu học bỏ chẩm điểm m&agrave; chỉ nhận x&eacute;t l&agrave;m gi&aacute;o vi&ecirc;n kh&ocirc;ng c&oacute; thời gian để nghỉ nữa. Nhiều khi chuy&ecirc;n m&ocirc;n giảng dạy chẳng c&oacute; thời gian t&igrave;m hiểu v&igrave; thời gian l&agrave;m sổ s&aacute;ch hết giờ rồi. C&ograve;n c&aacute;c thầy c&ocirc; THPT th&igrave; chạy theo cải tiến dạy học theo chủ đề. Mười thầy c&ocirc; th&igrave; ch&iacute;n thầy c&ocirc; n&oacute;i chẳng biết bộ nghĩ g&igrave; khi thay đổi như vậy trong ho&agrave;n cảnh hiện nay bởi vẫn s&aacute;ch đ&oacute;, kiến thức đ&oacute;. Chỉ l&agrave; lấy mấy b&agrave;i gộp lại cho th&agrave;nh một nh&oacute;m v&agrave; gọi l&agrave; chủ đề trong khi nội dung vẫn thế. Tại sao kh&ocirc;ng cải c&aacute;ch s&aacute;ch gi&aacute;o khoa trước rồi h&atilde;y chủ đề hay chủ điểm.</p>\r\n<p class="Normal" style="margin: 0px 0px 1em; padding: 0px; line-height: 18px; color: rgb(51, 51, 51); font-family: arial; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; orphans: auto; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255);">Cuộc sống gi&aacute;o vi&ecirc;n l&agrave; thế đ&oacute; c&aacute;c bạn. C&aacute;c bạn h&atilde;y suy nghĩ v&agrave; cảm th&ocirc;ng nếu một ng&agrave;y n&agrave;o đ&oacute; lại c&oacute; một thầy c&ocirc; bị đưa l&ecirc;n mạng v&igrave; điều g&igrave; đ&oacute; kh&ocirc;ng đ&uacute;ng với học sinh. Xin thưa rằng nếu c&aacute;c em ngoan chẳng thầy c&ocirc; n&agrave;o mắng, chửi, đ&aacute;nh c&aacute;c em cả. C&aacute;i g&igrave; cũng c&oacute; nguy&ecirc;n nh&acirc;n của n&oacute; cả. H&atilde;y nh&igrave;n từ hai ph&iacute;a, đặt m&igrave;nh v&agrave;o l&agrave; người trong cuộc để th&ocirc;ng cảm hơn cho c&aacute;c thầy c&ocirc;.</p>\r\n<p class="Normal" style="margin: 0px 0px 1em; padding: 0px; line-height: 18px; color: rgb(51, 51, 51); font-family: arial; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; orphans: auto; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255);">Ng&agrave;y nh&agrave; gi&aacute;o Việt Nam đến rồi. Biết bao thầy c&ocirc; đang mong chờ để được nhận những lời ch&uacute;c ch&acirc;n th&agrave;nh từ c&aacute;c học sinh, c&aacute;c vị phụ huynh, nhưng c&oacute; biết bao thầy c&ocirc; lại kh&ocirc;ng mong đến ng&agrave;y n&agrave;y bởi họ phải nhận những chiếc phong b&igrave; chứa nhiều tiền, thứ m&agrave; ai cũng muốn c&oacute; nhiều nhưng lại kh&ocirc;ng muốn nhận trong ho&agrave;n cảnh n&agrave;y. Bởi trong số đ&oacute; chỉ c&oacute; rất &iacute;t l&agrave; ch&acirc;n th&agrave;nh, c&ograve;n lại l&agrave; c&oacute; l&yacute; do cả đ&oacute;.</p>\r\n<p class="Normal" style="margin: 0px 0px 1em; padding: 0px; line-height: 18px; color: rgb(51, 51, 51); font-family: arial; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; orphans: auto; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255);">Nh&acirc;n ng&agrave;y nh&agrave; gi&aacute;o Việt Nam t&ocirc;i xin gửi những d&ograve;ng n&agrave;y đến c&aacute;c bạn kh&ocirc;ng l&agrave; gi&aacute;o vi&ecirc;n để hiểu hơn, th&ocirc;ng cảm hơn cho c&aacute;i nghề của ch&uacute;ng t&ocirc;i.</p>\r\n<p class="Normal" style="margin: 0px 0px 1em; padding: 0px; line-height: 18px; color: rgb(51, 51, 51); font-family: arial; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; orphans: auto; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-align: right; background-color: rgb(255, 255, 255);"><strong style="margin: 0px; padding: 0px;">Thanh Y</strong></p>', '', 0, 1416304288, 1, 0, 0, 0, 13, 23, 1, NULL, NULL, NULL);

